import axios from "axios";

const API_URL =
  process.env.REACT_APP_API_URL || "http://localhost:5000/api";

/* ================= AXIOS INSTANCE ================= */
const api = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

/* ================= REQUEST INTERCEPTOR ================= */
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

/* ================= RESPONSE INTERCEPTOR ================= */
/**
 * IMPORTANT:
 * Components receive FINAL data
 * NOT Axios response
 */
api.interceptors.response.use(
  (response) => response.data,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem("token");
      window.location.href = "/login";
    }
    return Promise.reject(
      error.response?.data || { message: error.message }
    );
  }
);

/* ================= HELPER ================= */
const cleanParams = (params = {}) => {
  const cleaned = {};
  Object.keys(params).forEach((key) => {
    if (
      params[key] !== "" &&
      params[key] !== undefined &&
      params[key] !== null
    ) {
      cleaned[key] = params[key];
    }
  });
  return cleaned;
};

/* ================= AUTH API ================= */
export const authAPI = {
  register: (data) => api.post("/auth/register", data),
  login: (data) => api.post("/auth/login", data),
  getMe: () => api.get("/auth/me"),
  updateProfile: (data) => api.put("/auth/update-profile", data),
  changePassword: (data) => api.put("/auth/change-password", data),
};

/* ================= ARTICLES API ================= */
export const articlesAPI = {
  getAll: (params = {}) =>
    api.get("/articles", { params: cleanParams(params) }),

  getBySlug: (slug) =>
    api.get(`/articles/${slug}`),

  getLatest: (limit = 10) =>
    api.get("/articles/latest", { params: { limit } }),

  getTrending: (limit = 5) =>
    api.get("/articles/trending", { params: { limit } }),
};

/* ================= MCQS API ================= */
export const mcqsAPI = {
  getAll: (params = {}) =>
    api.get("/mcqs", { params: cleanParams(params) }),

  getDailyQuiz: () =>
    api.get("/mcqs/daily-quiz"),

  submit: (payload) =>
    api.post("/mcqs/submit", payload),
};

/* ================= USERS API ================= */
export const usersAPI = {
  getProfile: () => api.get("/users/profile"),
  updateProfile: (data) => api.put("/users/profile", data),
  getSavedArticles: () => api.get("/users/saved-articles"),
};

/* ================= ADMIN API ================= */
export const adminAPI = {
  getDashboardStats: () => api.get("/admin/dashboard-stats"),
  getUsers: () => api.get("/admin/users"),
  getArticles: () => api.get("/admin/articles"),
};

/* ================= EXPORT DEFAULT ================= */
export default api;
