import React, { useState, useEffect } from "react";
import "./MCQCard.css";

const MCQCard = ({ mcq, selectedAnswer, onAnswer }) => {
  const [selected, setSelected] = useState(selectedAnswer ?? null);

  useEffect(() => {
    setSelected(selectedAnswer ?? null);
  }, [selectedAnswer]);

  const isAnswered = selected !== null;
  const isCorrect = selected === mcq.correctAnswer;

  const handleClick = (index) => {
    if (isAnswered) return;
    setSelected(index);
    onAnswer(mcq._id, index);
  };

  const getClass = (index) => {
    if (!isAnswered) return "option";
    if (index === mcq.correctAnswer) return "option correct";
    if (index === selected && index !== mcq.correctAnswer)
      return "option wrong";
    return "option";
  };

  return (
    <div className="mcq-card">
      {/* Header */}
      <div className="mcq-header">
        <span className="mcq-category">{mcq.category}</span>
        <span className={`mcq-difficulty ${mcq.difficulty?.toLowerCase()}`}>
          {mcq.difficulty}
        </span>
      </div>

      {/* Question */}
      <h3 className="mcq-question">{mcq.question}</h3>

      {/* Options */}
      <div className="mcq-options">
        {mcq.options.map((option, index) => (
          <div
            key={index}
            className={getClass(index)}
            onClick={() => handleClick(index)}
          >
            <span className="option-label">
              {String.fromCharCode(65 + index)}.
            </span>
            <span className="option-text">{option}</span>

            {isAnswered && index === mcq.correctAnswer && (
              <span className="option-icon correct-icon">✔</span>
            )}
            {isAnswered &&
              index === selected &&
              index !== mcq.correctAnswer && (
                <span className="option-icon wrong-icon">✖</span>
              )}
          </div>
        ))}
      </div>

      {/* Explanation */}
      {isAnswered && (
        <div className="mcq-explanation">
          <strong>{isCorrect ? "✅ Correct" : "❌ Incorrect"}</strong>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
};

export default MCQCard;
