import React, { useState } from 'react';
import { useQuery, useMutation } from 'react-query';
import { mcqsAPI } from '../services/api';
import MCQCard from '../components/MCQCard';
import { toast } from 'react-toastify';
import { FiTarget, FiAward, FiRefreshCw } from 'react-icons/fi';
import './DailyQuiz.css';

const DailyQuiz = () => {
  const [quizStarted, setQuizStarted] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState(null);

  const { data: quizData, isLoading, refetch } = useQuery(
    'dailyQuiz',
    () => mcqsAPI.getDailyQuiz(),
    { enabled: quizStarted }
  );

  const submitMutation = useMutation(
    (answers) => mcqsAPI.submit({ answers }),
    {
      onSuccess: (data) => {
        setResults(data.data);
        setShowResults(true);
        toast.success(`Quiz completed! Score: ${data.data.score}%`);
      },
      onError: () => {
        toast.error('Failed to submit quiz');
      }
    }
  );

  const startQuiz = () => {
    setQuizStarted(true);
    setCurrentQuestion(0);
    setAnswers([]);
    setShowResults(false);
    setResults(null);
  };

  const handleAnswer = (mcqId, selectedAnswer) => {
    const newAnswers = [...answers];
    newAnswers[currentQuestion] = { mcqId, selectedAnswer };
    setAnswers(newAnswers);
  };

  const handleNext = () => {
    if (!answers[currentQuestion]) {
      toast.error('Please select an answer');
      return;
    }
    
    if (currentQuestion < quizData.data.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const handleSubmit = () => {
    if (answers.length < quizData.data.length) {
      toast.error('Please answer all questions');
      return;
    }
    submitMutation.mutate(answers);
  };

  const restartQuiz = () => {
    setQuizStarted(false);
    setCurrentQuestion(0);
    setAnswers([]);
    setShowResults(false);
    setResults(null);
    refetch();
  };

  if (!quizStarted) {
    return (
      <div className="daily-quiz-page">
        <div className="quiz-start-screen">
          <div className="start-card">
            <FiTarget className="start-icon" />
            <h1>Daily Quiz Challenge</h1>
            <p>Test your knowledge with 10 carefully selected questions</p>
            <div className="quiz-info">
              <div className="info-item">
                <strong>10</strong>
                <span>Questions</span>
              </div>
              <div className="info-item">
                <strong>All Topics</strong>
                <span>Mixed Categories</span>
              </div>
              <div className="info-item">
                <strong>10 mins</strong>
                <span>Suggested Time</span>
              </div>
            </div>
            <button className="btn btn-primary btn-lg" onClick={startQuiz}>
              Start Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Preparing your quiz...</p>
      </div>
    );
  }

  if (showResults && results) {
    return (
      <div className="daily-quiz-page">
        <div className="quiz-results-screen">
          <div className="results-card">
            <FiAward className="results-icon" />
            <h1>Quiz Completed!</h1>
            
            <div className="score-circle">
              <div className="score-value">{results.score}%</div>
              <div className="score-label">Your Score</div>
            </div>

            <div className="results-stats">
              <div className="stat-box correct">
                <div className="stat-number">{results.correctAnswers}</div>
                <div className="stat-label">Correct</div>
              </div>
              <div className="stat-box incorrect">
                <div className="stat-number">{results.incorrectAnswers}</div>
                <div className="stat-label">Incorrect</div>
              </div>
              <div className="stat-box total">
                <div className="stat-number">{results.totalQuestions}</div>
                <div className="stat-label">Total</div>
              </div>
            </div>

            <div className="results-message">
              {results.score >= 80 ? (
                <p className="excellent">üéâ Excellent! You're doing great!</p>
              ) : results.score >= 60 ? (
                <p className="good">üëç Good job! Keep practicing!</p>
              ) : (
                <p className="needs-work">üí™ Keep learning! Practice makes perfect!</p>
              )}
            </div>

            <div className="detailed-results">
              <h3>Detailed Results</h3>
              {results.results.map((result, index) => (
                <MCQCard
                  key={index}
                  mcq={{
                    ...result,
                    options: quizData.data[index].options,
                    difficulty: quizData.data[index].difficulty,
                    category: quizData.data[index].category
                  }}
                  showAnswer={true}
                  selectedAnswer={result.selectedAnswer}
                />
              ))}
            </div>

            <button className="btn btn-primary btn-lg" onClick={restartQuiz}>
              <FiRefreshCw /> Take Another Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  const currentMCQ = quizData.data[currentQuestion];
  const progress = ((currentQuestion + 1) / quizData.data.length) * 100;

  return (
    <div className="daily-quiz-page">
      <div className="quiz-container">
        <div className="quiz-header">
          <div className="quiz-progress">
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${progress}%` }}></div>
            </div>
            <div className="progress-text">
              Question {currentQuestion + 1} of {quizData.data.length}
            </div>
          </div>
        </div>

        <div className="quiz-question">
          <MCQCard
            mcq={currentMCQ}
            onAnswer={handleAnswer}
            selectedAnswer={answers[currentQuestion]?.selectedAnswer}
          />
        </div>

        <div className="quiz-navigation">
          <button
            className="btn btn-secondary"
            onClick={handlePrevious}
            disabled={currentQuestion === 0}
          >
            Previous
          </button>

          <div className="answer-indicators">
            {quizData.data.map((_, index) => (
              <div
                key={index}
                className={`indicator ${answers[index] ? 'answered' : ''} ${index === currentQuestion ? 'active' : ''}`}
                onClick={() => setCurrentQuestion(index)}
              >
                {index + 1}
              </div>
            ))}
          </div>

          {currentQuestion < quizData.data.length - 1 ? (
            <button className="btn btn-primary" onClick={handleNext}>
              Next
            </button>
          ) : (
            <button 
              className="btn btn-primary" 
              onClick={handleSubmit}
              disabled={submitMutation.isLoading}
            >
              {submitMutation.isLoading ? 'Submitting...' : 'Submit Quiz'}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default DailyQuiz;
