import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response.data,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error.response?.data || error.message);
  }
);

// Auth API
export const authAPI = {
  register: (data) => api.post('/auth/register', data),
  login: (data) => api.post('/auth/login', data),
  getMe: () => api.get('/auth/me'),
  updateProfile: (data) => api.put('/auth/update-profile', data),
  changePassword: (data) => api.put('/auth/change-password', data),
  forgotPassword: (data) => api.post('/auth/forgot-password', data),
  resetPassword: (token, data) => api.post(`/auth/reset-password/${token}`, data),
};

// Articles API
export const articlesAPI = {
  getAll: (params) => api.get('/articles', { params }),
  getLatest: (limit = 10) => api.get('/articles/latest', { params: { limit } }),
  getTrending: (limit = 5) => api.get('/articles/trending', { params: { limit } }),
  getBySlug: (slug) => api.get(`/articles/${slug}`),
  getByCategory: (category, params) => api.get(`/articles/category/${category}`, { params }),
  create: (data) => api.post('/articles', data),
  update: (id, data) => api.put(`/articles/${id}`, data),
  delete: (id) => api.delete(`/articles/${id}`),
  like: (id) => api.post(`/articles/${id}/like`),
};

// MCQs API
export const mcqsAPI = {
  getAll: (params) => api.get('/mcqs', { params }),
  getDailyQuiz: (params) => api.get('/mcqs/daily-quiz', { params }),
  submit: (data) => api.post('/mcqs/submit', data),
  getCategories: () => api.get('/mcqs/categories'),
  getStats: () => api.get('/mcqs/stats'),
  create: (data) => api.post('/mcqs', data),
  update: (id, data) => api.put(`/mcqs/${id}`, data),
  delete: (id) => api.delete(`/mcqs/${id}`),
};

// Users API
export const usersAPI = {
  saveArticle: (articleId) => api.post(`/users/save-article/${articleId}`),
  unsaveArticle: (articleId) => api.delete(`/users/save-article/${articleId}`),
  getSavedArticles: () => api.get('/users/saved-articles'),
  getQuizHistory: () => api.get('/users/quiz-history'),
};

// Newsletter API
export const newsletterAPI = {
  subscribe: (data) => api.post('/newsletter/subscribe', data),
  unsubscribe: (data) => api.post('/newsletter/unsubscribe', data),
};

// Admin API
export const adminAPI = {
  getDashboardStats: () => api.get('/admin/dashboard-stats'),
  getUsers: (params) => api.get('/admin/users', { params }),
  updateUser: (id, data) => api.put(`/admin/users/${id}`, data),
  deleteUser: (id) => api.delete(`/admin/users/${id}`),
};

export default api;
