import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import { articlesAPI, usersAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-toastify';
import { format } from 'date-fns';
import { FiCalendar, FiUser, FiTag, FiBookmark, FiHeart, FiShare2, FiArrowLeft } from 'react-icons/fi';
import './ArticleDetail.css';

const ArticleDetail = () => {
  const { slug } = useParams();
  const { user, isAuthenticated } = useAuth();
  const [isSaved, setIsSaved] = useState(false);

  const { data, isLoading, error } = useQuery(
    ['article', slug],
    () => articlesAPI.getBySlug(slug),
    {
      retry: 1,
      onSuccess: (data) => {
        // Check if article is saved
        if (user?.savedArticles?.some(id => id === data.data._id)) {
          setIsSaved(true);
        }
      },
      onError: (err) => {
        console.error('Error loading article:', err);
      }
    }
  );

  const handleSaveArticle = async () => {
    if (!isAuthenticated()) {
      toast.error('Please login to save articles');
      return;
    }

    try {
      if (isSaved) {
        await usersAPI.unsaveArticle(data.data._id);
        setIsSaved(false);
        toast.success('Article removed from saved');
      } else {
        await usersAPI.saveArticle(data.data._id);
        setIsSaved(true);
        toast.success('Article saved!');
      }
    } catch (error) {
      toast.error('Failed to save article');
      console.error('Save error:', error);
    }
  };

  const handleLike = async () => {
    if (!isAuthenticated()) {
      toast.error('Please login to like articles');
      return;
    }

    try {
      await articlesAPI.like(data.data._id);
      toast.success('Article liked!');
      // Optionally refresh the data to show updated like count
    } catch (error) {
      toast.error('Failed to like article');
      console.error('Like error:', error);
    }
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: data.data.title,
        text: data.data.summary,
        url: window.location.href
      }).catch(err => console.log('Share failed:', err));
    } else {
      navigator.clipboard.writeText(window.location.href);
      toast.success('Link copied to clipboard!');
    }
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading article...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Article not found</h2>
        <p>The article you're looking for doesn't exist or has been removed.</p>
        <p className="error-details">{error?.message || 'Failed to load article'}</p>
        <Link to="/current-affairs" className="btn btn-primary">
          Back to Articles
        </Link>
      </div>
    );
  }

  if (!data || !data.data) {
    return (
      <div className="error-container">
        <h2>Article not found</h2>
        <Link to="/current-affairs" className="btn btn-primary">
          Back to Articles
        </Link>
      </div>
    );
  }

  const article = data.data;

  return (
    <div className="article-detail-page">
      <div className="container">
        <Link to="/current-affairs" className="back-link">
          <FiArrowLeft /> Back to Articles
        </Link>

        <article className="article-content">
          {/* Header */}
          <header className="article-header">
            <div className="article-category-badge">{article.category}</div>
            <h1 className="article-title">{article.title}</h1>
            
            <div className="article-meta">
              <div className="meta-item">
                <FiCalendar />
                <span>{format(new Date(article.publishDate), 'MMMM dd, yyyy')}</span>
              </div>
              <div className="meta-item">
                <FiUser />
                <span>{article.author?.name || 'Admin'}</span>
              </div>
              <div className="meta-item">
                <FiTag />
                <span>{article.viewCount || 0} views</span>
              </div>
            </div>

            {article.examRelevance && article.examRelevance.length > 0 && (
              <div className="exam-tags">
                <strong>Relevant for:</strong>
                {article.examRelevance.map((exam, index) => (
                  <span key={index} className="exam-tag">{exam}</span>
                ))}
              </div>
            )}
          </header>

          {/* Featured Image */}
          {article.featuredImage?.url && (
            <div className="featured-image">
              <img 
                src={article.featuredImage.url} 
                alt={article.featuredImage.alt || article.title}
                onError={(e) => e.target.style.display = 'none'} 
              />
            </div>
          )}

          {/* Summary */}
          {article.summary && (
            <div className="article-summary">
              <strong>Summary:</strong> {article.summary}
            </div>
          )}

          {/* Content */}
          <div 
            className="article-body"
            dangerouslySetInnerHTML={{ __html: article.content }}
          />

          {/* Tags */}
          {article.tags && article.tags.length > 0 && (
            <div className="article-tags">
              <strong>Tags:</strong>
              {article.tags.map((tag, index) => (
                <span key={index} className="tag">{tag}</span>
              ))}
            </div>
          )}

          {/* Action Buttons */}
          <div className="article-actions">
            <button 
              className={`action-btn ${isSaved ? 'active' : ''}`}
              onClick={handleSaveArticle}
            >
              <FiBookmark /> {isSaved ? 'Saved' : 'Save'}
            </button>
            <button className="action-btn" onClick={handleLike}>
              <FiHeart /> Like ({article.likes || 0})
            </button>
            <button className="action-btn" onClick={handleShare}>
              <FiShare2 /> Share
            </button>
          </div>
        </article>

        {/* Related Articles */}
        {article.relatedArticles && article.relatedArticles.length > 0 && (
          <section className="related-articles">
            <h2>Related Articles</h2>
            <div className="related-grid">
              {article.relatedArticles.map((related) => (
                <Link 
                  to={`/article/${related.slug}`} 
                  key={related._id}
                  className="related-card"
                >
                  {related.featuredImage?.url && (
                    <img 
                      src={related.featuredImage.url} 
                      alt={related.title}
                      onError={(e) => e.target.style.display = 'none'}
                    />
                  )}
                  <div className="related-content">
                    <span className="related-category">{related.category}</span>
                    <h3>{related.title}</h3>
                    <p>{related.summary}</p>
                  </div>
                </Link>
              ))}
            </div>
          </section>
        )}
      </div>
    </div>
  );
};

export default ArticleDetail;
