import React from "react";
import {
  FiCheck,
  FiX,
} from "react-icons/fi";
import "./MCQCard.css";

const MCQCard = ({
  mcq,
  onAnswer,
  selectedAnswer,
  showAnswer = false,
}) => {
  if (!mcq) return null;

  const handleClick = (option) => {
    if (showAnswer) return; // ❌ lock ONLY in review mode
    onAnswer(mcq._id, option);
  };

  return (
    <div className="mcq-card">
      <div className="mcq-header">
        <span className="mcq-category">{mcq.category}</span>
        {mcq.difficulty && (
          <span className={`mcq-difficulty ${mcq.difficulty.toLowerCase()}`}>
            {mcq.difficulty}
          </span>
        )}
      </div>

      <h3 className="mcq-question">{mcq.question}</h3>

      <div className="mcq-options">
        {mcq.options.map((option, index) => {
          const isSelected = selectedAnswer === option;
          const isCorrect = showAnswer && option === mcq.correctAnswer;
          const isWrong =
            showAnswer &&
            isSelected &&
            option !== mcq.correctAnswer;

          return (
            <button
              key={index}
              className={`mcq-option
                ${isSelected ? "selected" : ""}
                ${isCorrect ? "correct" : ""}
                ${isWrong ? "wrong" : ""}
              `}
              onClick={() => handleClick(option)}
            >
              <span className="option-label">
                {String.fromCharCode(65 + index)}.
              </span>
              <span className="option-text">{option}</span>

              {isCorrect && <FiCheck className="icon correct-icon" />}
              {isWrong && <FiX className="icon wrong-icon" />}
            </button>
          );
        })}
      </div>

      {showAnswer && (
        <div className="mcq-explanation">
          <strong>
            {selectedAnswer === mcq.correctAnswer ? "Correct" : "Incorrect"}
          </strong>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
};

export default MCQCard;
