import React from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import "../styles/gktoday-home.css";

const Home = () => {
  const { data, isLoading, isError } = useQuery(
    ["articles", "latest"],
    () => articlesAPI.getLatest(10)
  );

  const articles = data?.data || [];

  return (
    <div className="gk-container">
      {/* MAIN */}
      <main className="gk-main">
        <h1 className="gk-title">GKToday</h1>

        {isLoading && <p>Loading current affairs...</p>}
        {isError && <p>Failed to load articles.</p>}

        {articles.map((article) => (
          <Link
            to={`/article/${article.slug}`}
            key={article._id}
            className="gk-article"
          >
            <img
              src={article.featuredImage || "/placeholder.jpg"}
              alt={article.title}
            />

            <div className="gk-article-content">
              <h2>{article.title}</h2>
              <p>{article.summary}</p>
              <span className="gk-date">
                ðŸ“… {new Date(article.createdAt).toDateString()}
              </span>
            </div>
          </Link>
        ))}

        <div className="gk-older">Older Posts â†’</div>
      </main>

      {/* SIDEBAR */}
      <aside className="gk-sidebar">
        <div className="sidebar-box">
          <h3>Daily MCQs</h3>
          <ul>
            <li>
              <Link to="/daily-quiz">Daily Current Affairs Quiz</Link>
            </li>
            <li>Monthly MCQs</li>
            <li>Topic-wise MCQs</li>
          </ul>
        </div>

        <div className="sidebar-box">
          <h3>E-Books</h3>
          <ul>
            <li>Current Affairs Monthly PDF</li>
            <li>CA Articles + MCQs</li>
            <li>Yearly CA PDF</li>
          </ul>
        </div>
      </aside>
    </div>
  );
};

export default Home;
