import React, { useState } from "react";
import { useQuery, useMutation } from "react-query";
import { mcqsAPI } from "../services/api";
import MCQCard from "../components/MCQCard";
import { toast } from "react-toastify";
import { FiTarget, FiAward, FiRefreshCw } from "react-icons/fi";
import "./DailyQuiz.css";

const DailyQuiz = () => {
  const [quizStarted, setQuizStarted] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const [resultData, setResultData] = useState(null);

  /* ================= FETCH QUIZ ================= */
  const {
    data: quizResponse,
    isLoading,
    isError,
    refetch,
  } = useQuery("dailyQuiz", mcqsAPI.getDailyQuiz, {
    enabled: quizStarted,
  });

  const quiz = quizResponse?.data || [];

  /* ================= SUBMIT QUIZ ================= */
  const submitMutation = useMutation(mcqsAPI.submit, {
    onSuccess: (res) => {
      setResultData(res);
      setShowResults(true);
      toast.success(`Score: ${res.score}%`);
    },
    onError: () => toast.error("Quiz submission failed"),
  });

  /* ================= HANDLERS ================= */
  const startQuiz = () => {
    setQuizStarted(true);
    setCurrentIndex(0);
    setAnswers([]);
    setShowResults(false);
    setResultData(null);
  };

  const selectAnswer = (mcqId, selectedAnswer) => {
    const updated = [...answers];
    updated[currentIndex] = { mcqId, selectedAnswer };
    setAnswers(updated);
  };

  const next = () => {
    if (!answers[currentIndex]) {
      toast.error("Please select an answer");
      return;
    }
    setCurrentIndex((prev) => prev + 1);
  };

  const submitQuiz = () => {
    if (answers.length !== quiz.length) {
      toast.error("Answer all questions");
      return;
    }
    submitMutation.mutate({ answers });
  };

  const restart = () => {
    setQuizStarted(false);
    setCurrentIndex(0);
    setAnswers([]);
    setShowResults(false);
    setResultData(null);
    refetch();
  };

  /* ================= START SCREEN ================= */
  if (!quizStarted) {
    return (
      <div className="daily-quiz-page">
        <div className="quiz-start-screen">
          <FiTarget className="start-icon" />
          <h1>Daily Quiz</h1>
          <p>10 random MCQs from current affairs</p>
          <button className="btn btn-primary" onClick={startQuiz}>
            Start Quiz
          </button>
        </div>
      </div>
    );
  }

  /* ================= LOADING ================= */
  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner" />
        <p>Preparing quiz...</p>
      </div>
    );
  }

  /* ================= ERROR ================= */
  if (isError) {
    return (
      <div className="daily-quiz-page error-box">
        <p>Failed to load quiz. Try again later.</p>
        <button className="btn btn-secondary" onClick={restart}>
          Retry
        </button>
      </div>
    );
  }

  /* ================= EMPTY QUIZ ================= */
  if (!quiz.length) {
    return (
      <div className="daily-quiz-page empty-state">
        <FiTarget size={48} />
        <h2>No quiz available today</h2>
        <p>Please check back later.</p>
        <button className="btn btn-secondary" onClick={restart}>
          Refresh
        </button>
      </div>
    );
  }

  /* ================= RESULT SCREEN ================= */
  if (showResults && resultData) {
    return (
      <div className="daily-quiz-page quiz-results-screen">
        <FiAward className="results-icon" />
        <h1>Your Score</h1>
        <div className="score-circle">{resultData.score}%</div>

        <p>
          Correct: {resultData.correctAnswers} /{" "}
          {resultData.totalQuestions}
        </p>

        <button className="btn btn-primary" onClick={restart}>
          <FiRefreshCw /> Try Again
        </button>
      </div>
    );
  }

  /* ================= QUIZ UI ================= */
  const currentMCQ = quiz[currentIndex];
  const progress = ((currentIndex + 1) / quiz.length) * 100;

  return (
    <div className="daily-quiz-page">
      <div className="quiz-container">
        <div className="progress-bar">
          <div
            className="progress-fill"
            style={{ width: `${progress}%` }}
          />
        </div>

        <MCQCard
          mcq={currentMCQ}
          selectedAnswer={answers[currentIndex]?.selectedAnswer}
          onAnswer={selectAnswer}
        />

        <div className="quiz-actions">
          {currentIndex < quiz.length - 1 ? (
            <button className="btn btn-primary" onClick={next}>
              Next
            </button>
          ) : (
            <button
              className="btn btn-success"
              onClick={submitQuiz}
              disabled={submitMutation.isLoading}
            >
              Submit Quiz
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default DailyQuiz;
