import axios from "axios";

const API_URL =
  process.env.REACT_APP_API_URL || "http://localhost:5000/api";

/* ================= AXIOS INSTANCE ================= */
const api = axios.create({
  baseURL: API_URL,
  headers: { "Content-Type": "application/json" },
});

/* ================= REQUEST INTERCEPTOR ================= */
api.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

/* ================= RESPONSE INTERCEPTOR ================= */
api.interceptors.response.use(
  (response) => response.data,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem("token");
      window.location.href = "/login";
    }
    return Promise.reject(
      error.response?.data || { message: error.message }
    );
  }
);

/* ================= PARAM CLEANER ================= */
const cleanParams = (params = {}) => {
  const cleaned = {};
  Object.entries(params).forEach(([key, value]) => {
    if (value !== "" && value !== null && value !== undefined) {
      cleaned[key] = value;
    }
  });
  return cleaned;
};

/* ================= ARTICLES API ================= */
export const articlesAPI = {
  /** PAGE-BASED (SEO, URL pagination) */
  getAll: (params = {}) =>
    api.get("/articles", { params: cleanParams(params) }),

  /** CURSOR-BASED (FAST, BIG DATA) */
  getByCursor: ({ cursor, limit = 9, ...filters }) =>
    api.get("/articles", {
      params: cleanParams({
        cursor,
        limit,
        ...filters,
      }),
    }),

  /** SINGLE ARTICLE */
  getBySlug: (slug) => api.get(`/articles/${slug}`),

  /** HOME PAGE */
  getLatest: (limit = 10) =>
    api.get("/articles/latest", { params: { limit } }),

  getTrending: (limit = 5) =>
    api.get("/articles/trending", { params: { limit } }),

  /** CRUD */
  create: (data) => api.post("/articles", data),
  update: (id, data) => api.put(`/articles/${id}`, data),
  delete: (id) => api.delete(`/articles/${id}`),

  /** INTERACTIONS */
  like: (id) => api.post(`/articles/${id}/like`),
};

/* ================= EXPORT ================= */
export default api;
