import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { useAuth } from '../context/AuthContext';
import { usersAPI, authAPI } from '../services/api';
import { toast } from 'react-toastify';
import { Link } from 'react-router-dom';
import { FiUser, FiBookmark, FiAward, FiSettings, FiLock } from 'react-icons/fi';
import './Profile.css';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [profileData, setProfileData] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    city: user?.city || ''
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const { data: savedArticles } = useQuery(
    'savedArticles',
    () => usersAPI.getSavedArticles(),
    { enabled: !!user }
  );

  const { data: quizHistory } = useQuery(
    'quizHistory',
    () => usersAPI.getQuizHistory(),
    { enabled: !!user }
  );

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      await authAPI.updateProfile(profileData);
      updateUser(profileData);
      toast.success('Profile updated successfully');
      setIsEditingProfile(false);
    } catch (error) {
      toast.error('Failed to update profile');
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }

    try {
      await authAPI.changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });
      toast.success('Password changed successfully');
      setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
    } catch (error) {
      toast.error(error.message || 'Failed to change password');
    }
  };

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-layout">
          {/* Sidebar */}
          <div className="profile-sidebar">
            <div className="profile-card">
              <div className="profile-avatar">
                {user?.name?.charAt(0).toUpperCase()}
              </div>
              <h2>{user?.name}</h2>
              <p className="profile-email">{user?.email}</p>
              <div className="profile-role">{user?.role}</div>
            </div>

            <nav className="profile-nav">
              <button
                className={`nav-item ${activeTab === 'overview' ? 'active' : ''}`}
                onClick={() => setActiveTab('overview')}
              >
                <FiUser /> Overview
              </button>
              <button
                className={`nav-item ${activeTab === 'saved' ? 'active' : ''}`}
                onClick={() => setActiveTab('saved')}
              >
                <FiBookmark /> Saved Articles
              </button>
              <button
                className={`nav-item ${activeTab === 'quiz' ? 'active' : ''}`}
                onClick={() => setActiveTab('quiz')}
              >
                <FiAward /> Quiz History
              </button>
              <button
                className={`nav-item ${activeTab === 'settings' ? 'active' : ''}`}
                onClick={() => setActiveTab('settings')}
              >
                <FiSettings /> Settings
              </button>
            </nav>
          </div>

          {/* Main Content */}
          <div className="profile-content">
            {activeTab === 'overview' && (
              <div className="content-section">
                <h2>Profile Overview</h2>
                
                <div className="stats-grid">
                  <div className="stat-card">
                    <FiBookmark className="stat-icon" />
                    <div className="stat-value">{savedArticles?.data?.length || 0}</div>
                    <div className="stat-label">Saved Articles</div>
                  </div>
                  <div className="stat-card">
                    <FiAward className="stat-icon" />
                    <div className="stat-value">{quizHistory?.data?.length || 0}</div>
                    <div className="stat-label">Quizzes Taken</div>
                  </div>
                </div>

                <div className="info-card">
                  <h3>Personal Information</h3>
                  <div className="info-grid">
                    <div className="info-item">
                      <strong>Name:</strong>
                      <span>{user?.name}</span>
                    </div>
                    <div className="info-item">
                      <strong>Email:</strong>
                      <span>{user?.email}</span>
                    </div>
                    <div className="info-item">
                      <strong>Phone:</strong>
                      <span>{user?.phone || 'Not provided'}</span>
                    </div>
                    <div className="info-item">
                      <strong>City:</strong>
                      <span>{user?.city || 'Not provided'}</span>
                    </div>
                  </div>

                  {user?.examPreparation && user.examPreparation.length > 0 && (
                    <div className="exam-prep">
                      <strong>Preparing for:</strong>
                      <div className="exam-tags">
                        {user.examPreparation.map((exam, index) => (
                          <span key={index} className="exam-tag">{exam}</span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}

            {activeTab === 'saved' && (
              <div className="content-section">
                <h2>Saved Articles</h2>
                {savedArticles?.data?.length > 0 ? (
                  <div className="saved-list">
                    {savedArticles.data.map((article) => (
                      <Link to={`/article/${article.slug}`} key={article._id} className="saved-item">
                        {article.featuredImage?.url && (
                          <img src={article.featuredImage.url} alt={article.title} />
                        )}
                        <div className="saved-content">
                          <span className="saved-category">{article.category}</span>
                          <h3>{article.title}</h3>
                          <p>{article.summary}</p>
                        </div>
                      </Link>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <p>No saved articles yet</p>
                    <Link to="/current-affairs" className="btn btn-primary">
                      Browse Articles
                    </Link>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'quiz' && (
              <div className="content-section">
                <h2>Quiz History</h2>
                {quizHistory?.data?.length > 0 ? (
                  <div className="quiz-history-list">
                    {quizHistory.data.slice(0, 10).map((attempt, index) => (
                      <div key={index} className="quiz-history-item">
                        <div className="quiz-info">
                          <strong>{attempt.mcq?.question}</strong>
                          <span className="quiz-category">{attempt.mcq?.category}</span>
                        </div>
                        <div className={`quiz-result ${attempt.isCorrect ? 'correct' : 'incorrect'}`}>
                          {attempt.isCorrect ? '✓ Correct' : '✗ Incorrect'}
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <p>No quiz attempts yet</p>
                    <Link to="/daily-quiz" className="btn btn-primary">
                      Take Daily Quiz
                    </Link>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'settings' && (
              <div className="content-section">
                <h2>Settings</h2>

                {/* Edit Profile */}
                <div className="settings-card">
                  <h3><FiUser /> Edit Profile</h3>
                  {isEditingProfile ? (
                    <form onSubmit={handleProfileUpdate}>
                      <div className="form-group">
                        <label>Name</label>
                        <input
                          type="text"
                          className="form-input"
                          value={profileData.name}
                          onChange={(e) => setProfileData({...profileData, name: e.target.value})}
                        />
                      </div>
                      <div className="form-group">
                        <label>Phone</label>
                        <input
                          type="tel"
                          className="form-input"
                          value={profileData.phone}
                          onChange={(e) => setProfileData({...profileData, phone: e.target.value})}
                        />
                      </div>
                      <div className="form-group">
                        <label>City</label>
                        <input
                          type="text"
                          className="form-input"
                          value={profileData.city}
                          onChange={(e) => setProfileData({...profileData, city: e.target.value})}
                        />
                      </div>
                      <div className="form-actions">
                        <button type="submit" className="btn btn-primary">Save Changes</button>
                        <button type="button" className="btn btn-secondary" onClick={() => setIsEditingProfile(false)}>
                          Cancel
                        </button>
                      </div>
                    </form>
                  ) : (
                    <button className="btn btn-outline" onClick={() => setIsEditingProfile(true)}>
                      Edit Profile
                    </button>
                  )}
                </div>

                {/* Change Password */}
                <div className="settings-card">
                  <h3><FiLock /> Change Password</h3>
                  <form onSubmit={handlePasswordChange}>
                    <div className="form-group">
                      <label>Current Password</label>
                      <input
                        type="password"
                        className="form-input"
                        value={passwordData.currentPassword}
                        onChange={(e) => setPasswordData({...passwordData, currentPassword: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label>New Password</label>
                      <input
                        type="password"
                        className="form-input"
                        value={passwordData.newPassword}
                        onChange={(e) => setPasswordData({...passwordData, newPassword: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label>Confirm New Password</label>
                      <input
                        type="password"
                        className="form-input"
                        value={passwordData.confirmPassword}
                        onChange={(e) => setPasswordData({...passwordData, confirmPassword: e.target.value})}
                        required
                      />
                    </div>
                    <button type="submit" className="btn btn-primary">Change Password</button>
                  </form>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
