import React from 'react';
import { Link } from 'react-router-dom';
import { format } from 'date-fns';
import './ArticleCard.css';

const ArticleCard = ({ article }) => {
  return (
    <div className="article-card">
      {article.featuredImage?.url && (
        <div className="article-image">
          <img src={article.featuredImage.url} alt={article.featuredImage.alt || article.title} />
        </div>
      )}
      <div className="article-content">
        <div className="article-meta">
          <span className="article-category">{article.category}</span>
          <span className="article-date">
            {format(new Date(article.publishDate), 'MMM dd, yyyy')}
          </span>
        </div>
        <Link to={`/article/${article.slug}`} className="article-title">
          <h3>{article.title}</h3>
        </Link>
        <p className="article-summary">{article.summary}</p>
        <div className="article-footer">
          <div className="article-tags">
            {article.examRelevance?.slice(0, 3).map((exam, index) => (
              <span key={index} className="tag">{exam}</span>
            ))}
          </div>
          <Link to={`/article/${article.slug}`} className="read-more">
            Read More â†’
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ArticleCard;
