import { useState } from "react";

export default function MCQCard({ mcq }) {
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [answered, setAnswered] = useState(false);

  const handleOptionClick = (index) => {
    if (answered) return;
    setSelectedIndex(index);
    setAnswered(true);
  };

  const getOptionClass = (index) => {
    if (!answered) return "option";

    if (index === mcq.correctAnswer) {
      return "option correct";
    }

    if (index === selectedIndex) {
      return "option wrong";
    }

    return "option";
  };

  return (
    <div className="mcq-card">
      {/* HEADER */}
      <div className="mcq-header">
        <span className="mcq-category">{mcq.category}</span>
        <span className={`mcq-difficulty ${mcq.difficulty?.toLowerCase()}`}>
          {mcq.difficulty}
        </span>
      </div>

      {/* QUESTION */}
      <div className="mcq-question">{mcq.question}</div>

      {/* OPTIONS */}
      <div className="mcq-options">
        {mcq.options.map((option, index) => (
          <div
            key={index}
            className={getOptionClass(index)}
            onClick={() => handleOptionClick(index)}
          >
            <span className="option-label">
              {String.fromCharCode(65 + index)}.
            </span>

            <span className="option-text">{option}</span>

            {answered && index === mcq.correctAnswer && (
              <span className="option-icon correct-icon">✓</span>
            )}

            {answered &&
              index === selectedIndex &&
              index !== mcq.correctAnswer && (
                <span className="option-icon wrong-icon">✕</span>
              )}
          </div>
        ))}
      </div>

      {/* EXPLANATION */}
      {answered && mcq.explanation && (
        <div className="mcq-explanation">
          <strong>Explanation</strong>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
}
