import React, { memo } from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import ArticleCard from "../components/ArticleCard";
import {
  FiBookOpen,
  FiAward,
  FiTrendingUp,
  FiTarget,
} from "react-icons/fi";
import "./Home.css";

const Home = () => {
  const {
    data: latestArticles,
    isLoading: latestLoading,
    isError: latestError,
  } = useQuery(
    ["articles", "latest", 6],
    () => articlesAPI.getLatest(6),
    { staleTime: 5 * 60 * 1000 }
  );

  const {
    data: trendingArticles,
    isLoading: trendingLoading,
    isError: trendingError,
  } = useQuery(
    ["articles", "trending", 3],
    () => articlesAPI.getTrending(3),
    { staleTime: 10 * 60 * 1000 }
  );

  return (
    <div className="home-page">
      {/* ================= HERO ================= */}
      <section className="hero-section">
        <div className="container">
          <div className="hero-content fade-in">
            <h1 className="hero-title">
              Master Current Affairs for <br />
              <span className="gradient-text">Competitive Exams</span>
            </h1>

            <p className="hero-subtitle">
              Daily current affairs, MCQ practice, and exam-oriented content for
              UPSC, SSC, Banking, Railway, and State PSC exams.
            </p>

            <div className="hero-buttons">
              <Link to="/current-affairs" className="btn btn-primary btn-lg">
                <FiBookOpen aria-hidden /> Explore Current Affairs
              </Link>
              <Link to="/daily-quiz" className="btn btn-outline btn-lg">
                <FiTarget aria-hidden /> Take Daily Quiz
              </Link>
            </div>

            <div className="hero-stats" aria-label="Platform statistics">
              <Stat number="10K+" label="Daily Visitors" />
              <Stat number="5K+" label="MCQs" />
              <Stat number="100%" label="Free Access" />
            </div>
          </div>
        </div>
      </section>

      {/* ================= FEATURES ================= */}
      <section className="features-section section">
        <div className="container">
          <h2 className="section-title">Why Choose NMAI?</h2>

          <div className="features-grid">
            <Feature icon="ðŸ“°" title="Daily Current Affairs">
              National & international coverage updated daily.
            </Feature>
            <Feature icon="âœ…" title="Practice MCQs">
              Thousands of exam-oriented questions with explanations.
            </Feature>
            <Feature icon="ðŸ“Š" title="Exam-Specific">
              UPSC, SSC, Banking, Railway & State PSC focused.
            </Feature>
            <Feature icon="ðŸŽ¯" title="Daily Quiz">
              10 hand-picked questions every day.
            </Feature>
            <Feature icon="ðŸ“±" title="Mobile Friendly">
              Study anywhere on any device.
            </Feature>
            <Feature icon="ðŸ†“" title="100% Free">
              No subscriptions. No paywalls.
            </Feature>
          </div>
        </div>
      </section>

      {/* ================= LATEST ARTICLES ================= */}
      <section className="latest-articles section">
        <div className="container">
          <div className="section-header">
            <div>
              <h2 className="section-title">Latest Current Affairs</h2>
              <p className="section-subtitle">
                Stay updated with the most recent developments
              </p>
            </div>
            <Link to="/current-affairs" className="view-all-link">
              View All <FiTrendingUp aria-hidden />
            </Link>
          </div>

          {latestLoading && <ArticleSkeletonGrid />}
          {latestError && (
            <ErrorBox message="Unable to load latest articles. Please try again." />
          )}

          {!latestLoading &&
            !latestError &&
            latestArticles?.data?.length > 0 && (
              <div className="articles-grid">
                {latestArticles.data.map((article) => (
                  <ArticleCard key={article._id} article={article} />
                ))}
              </div>
            )}

          {!latestLoading &&
            !latestError &&
            (!latestArticles?.data ||
              latestArticles.data.length === 0) && (
              <EmptyState message="No articles available yet. Check back soon!" />
            )}
        </div>
      </section>

      {/* ================= TRENDING ================= */}
      {!trendingLoading &&
        !trendingError &&
        trendingArticles?.data?.length > 0 && (
          <section className="trending-section section">
            <div className="container">
              <h2 className="section-title">
                <FiTrendingUp aria-hidden /> Trending This Week
              </h2>

              <div className="trending-grid">
                {trendingArticles.data.map((article, index) => (
                  <Link
                    to={`/article/${article.slug}`}
                    key={article._id}
                    className="trending-item"
                  >
                    <div className="trending-number">#{index + 1}</div>
                    <div className="trending-content">
                      <h3>{article.title}</h3>
                      <div className="trending-meta">
                        <span>{article.viewCount} views</span>
                        <span>{article.category}</span>
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            </div>
          </section>
        )}

      {/* ================= EXAM CATEGORIES ================= */}
      <section className="exam-categories section">
        <div className="container">
          <h2 className="section-title">Prepare by Exam</h2>

          <div className="categories-grid">
            {[
              "UPSC",
              "SSC",
              "Banking",
              "Railway",
              "State PSC",
              "BPSC",
              "UPPSC",
              "MPPSC",
            ].map((exam) => (
              <Link
                to={`/current-affairs?exam=${exam}`}
                key={exam}
                className="category-card"
              >
                <FiAward className="category-icon" aria-hidden />
                <h3>{exam}</h3>
                <p>View Content â†’</p>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* ================= CTA ================= */}
      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Start Your Preparation?</h2>
            <p>Join thousands of aspirants preparing with NMAI</p>

            <div className="cta-buttons">
              <Link to="/register" className="btn btn-primary btn-lg">
                Get Started Free
              </Link>
              <Link to="/about-us" className="btn btn-outline btn-lg">
                Learn More
              </Link>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;

/* ================= SUB-COMPONENTS ================= */

const Stat = memo(({ number, label }) => (
  <div className="stat-item">
    <div className="stat-number">{number}</div>
    <div className="stat-label">{label}</div>
  </div>
));

const Feature = memo(({ icon, title, children }) => (
  <div className="feature-card">
    <div className="feature-icon">{icon}</div>
    <h3>{title}</h3>
    <p>{children}</p>
  </div>
));

const ArticleSkeletonGrid = () => (
  <div className="articles-grid">
    {Array.from({ length: 6 }).map((_, i) => (
      <div key={i} className="article-skeleton" />
    ))}
  </div>
);

const ErrorBox = ({ message }) => (
  <div className="error-box" role="alert">
    {message}
  </div>
);

const EmptyState = ({ message }) => (
  <div className="empty-state">{message}</div>
);
