import React from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import "../styles/nmai-home.css"; // rename CSS if needed

const Home = () => {
  const { data, isLoading, isError } = useQuery(
    ["articles", "latest"],
    () => articlesAPI.getLatest(10),
    { staleTime: 5 * 60 * 1000 }
  );

  const articles = data?.data || [];

  return (
    <div className="gk-container">
      {/* MAIN CONTENT */}
      <main className="gk-main">
        <h1 className="gk-title">NMAI</h1>
        <p className="gk-subtitle">
          Daily Current Affairs for Competitive Exams
        </p>

        {isLoading && <p>Loading current affairs...</p>}
        {isError && <p>Failed to load articles.</p>}

        {articles.map((article) => (
          <Link
            to={`/article/${article.slug}`}
            key={article._id}
            className="gk-article"
          >
            <img
              src={article.featuredImage || "/placeholder.jpg"}
              alt={article.title}
            />

            <div className="gk-article-content">
              <h2>{article.title}</h2>
              <p>{article.summary}</p>
              <span className="gk-date">
                ðŸ“… {new Date(article.createdAt).toDateString()}
              </span>
            </div>
          </Link>
        ))}

        <div className="gk-older">
          <Link to="/current-affairs">Older Posts â†’</Link>
        </div>
      </main>

      {/* SIDEBAR */}
      <aside className="gk-sidebar">
        <div className="sidebar-box">
          <h3>Daily MCQs</h3>
          <ul>
            <li>
              <Link to="/daily-quiz">Daily Current Affairs Quiz</Link>
            </li>
            <li>
              <Link to="/monthly-mcqs">Monthly MCQs</Link>
            </li>
            <li>
              <Link to="/topic-mcqs">Topic-wise MCQs</Link>
            </li>
          </ul>
        </div>

        <div className="sidebar-box">
          <h3>Study Material</h3>
          <ul>
            <li>Monthly Current Affairs PDF</li>
            <li>CA + MCQs Compilation</li>
            <li>Yearly Current Affairs PDF</li>
          </ul>
        </div>
      </aside>
    </div>
  );
};

export default Home;
