import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import localArticles from "../data/articles";
import ArticleCard from "../components/ArticleCard";
import {
  FiBookOpen,
  FiTrendingUp,
  FiTarget,
} from "react-icons/fi";
import "./Home.css";
import "./GKTodayLayout.css";

/* ================= DATE HELPERS ================= */

const formatDate = (date) => {
  if (!date) return "";
  return new Date(date).toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
  });
};

const isSameDay = (a, b) =>
  new Date(a).toDateString() === new Date(b).toDateString();

const isToday = (date) =>
  date && isSameDay(date, new Date());

const isYesterday = (date) => {
  if (!date) return false;
  const y = new Date();
  y.setDate(y.getDate() - 1);
  return isSameDay(date, y);
};

const getDateLabel = (date) => {
  if (isToday(date)) return "Today";
  if (isYesterday(date)) return "Yesterday";
  return formatDate(date);
};

/* ================= HOME ================= */

const Home = () => {
  // ✅ ALWAYS arrays
  const [articles, setArticles] = useState([]);
  const [trending, setTrending] = useState([]);
  const [loading, setLoading] = useState(true);

  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("All");

  /* ================= FETCH ================= */

  useEffect(() => {
    let mounted = true;

    const fetchData = async () => {
      try {
        const [latestRes, trendingRes] = await Promise.all([
          articlesAPI.getLatest(20),
          articlesAPI.getTrending(5),
        ]);

        if (!mounted) return;

        // ✅ HARD SAFETY
        setArticles(
          Array.isArray(latestRes?.data?.data)
            ? latestRes.data.data
            : []
        );

        setTrending(
          Array.isArray(trendingRes?.data?.data)
            ? trendingRes.data.data
            : []
        );
      } catch (err) {
        console.error("API failed, using local fallback", err);

        // ✅ fallback only if API fails
        setArticles(Array.isArray(localArticles) ? localArticles : []);
        setTrending(
          Array.isArray(localArticles)
            ? localArticles.slice(0, 5)
            : []
        );
      } finally {
        if (mounted) setLoading(false);
      }
    };

    fetchData();
    return () => (mounted = false);
  }, []);

  /* ================= FILTER ================= */

  const safeArticles = Array.isArray(articles) ? articles : [];

  const filtered = safeArticles.filter((a) => {
    if (!a?.title) return false;

    const text =
      `${a.title} ${a.summary || ""}`.toLowerCase();

    const matchesSearch = text.includes(search.toLowerCase());
    const matchesCategory =
      category === "All" || a.category === category;

    return matchesSearch && matchesCategory;
  });

  const todayArticles = filtered.filter((a) =>
    isToday(a.publishDate || a.createdAt)
  );

  const yesterdayArticles = filtered.filter((a) =>
    isYesterday(a.publishDate || a.createdAt)
  );

  const earlierArticles = filtered.filter(
    (a) =>
      !isToday(a.publishDate || a.createdAt) &&
      !isYesterday(a.publishDate || a.createdAt)
  );

  /* ================= MCQs (STATIC) ================= */

  const mcqs = [
    { q: "Who presents the Union Budget in India?", a: "Finance Minister" },
    { q: "Which mission promotes AI development in India?", a: "National AI Mission" },
    { q: "Which institution controls monetary policy in India?", a: "Reserve Bank of India" },
  ];

  return (
    <div className="gk-layout">
      {/* ================= LEFT ================= */}
      <aside className="gk-sidebar left">
        <h3>Categories</h3>
        <ul>
          {[
            "All",
            "Economy",
            "Polity",
            "Science",
            "Technology",
            "Environment",
            "International",
            "National",
            "Defence",
          ].map((cat) => (
            <li
              key={cat}
              onClick={() => setCategory(cat)}
              className={category === cat ? "active" : ""}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      {/* ================= MAIN ================= */}
      <main className="gk-content">
        {/* HERO */}
        <section className="hero-section">
          <h1 className="hero-title">
            Master Current Affairs for <br />
            <span className="gradient-text">Competitive Exams</span>
          </h1>

          <p className="hero-subtitle">
            Daily current affairs, MCQs, and exam-focused content.
          </p>

          <div className="hero-buttons">
            <Link to="/current-affairs" className="btn btn-primary">
              <FiBookOpen /> Explore
            </Link>
            <Link to="/daily-quiz" className="btn btn-outline">
              <FiTarget /> Daily Quiz
            </Link>
          </div>
        </section>

        {/* ================= ARTICLES ================= */}
        <section className="section">
          <h2>Latest Current Affairs</h2>

          {loading && <p>Loading articles…</p>}

          {!loading && todayArticles.length > 0 && (
            <>
              <h3 className="group-title">Today</h3>
              <div className="articles-grid">
                {todayArticles.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{ ...a, dateLabel: getDateLabel(a.publishDate || a.createdAt) }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && yesterdayArticles.length > 0 && (
            <>
              <h3 className="group-title">Yesterday</h3>
              <div className="articles-grid">
                {yesterdayArticles.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{ ...a, dateLabel: getDateLabel(a.publishDate || a.createdAt) }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && earlierArticles.length > 0 && (
            <>
              <h3 className="group-title">Earlier</h3>
              <div className="articles-grid">
                {earlierArticles.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{ ...a, dateLabel: getDateLabel(a.publishDate || a.createdAt) }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && filtered.length === 0 && (
            <div className="empty-state">No matching articles found.</div>
          )}
        </section>

        {/* ================= TRENDING ================= */}
        {trending.length > 0 && (
          <section className="section">
            <h2><FiTrendingUp /> Trending</h2>
            <div className="trending-grid">
              {trending.map((a, i) => (
                <Link key={a._id} to={`/article/${a.slug}`} className="trending-item">
                  <span className="trending-number">#{i + 1}</span>
                  <div>
                    <h3>{a.title}</h3>
                    <small>{getDateLabel(a.publishDate || a.createdAt)}</small>
                  </div>
                </Link>
              ))}
            </div>
          </section>
        )}

        {/* ================= MCQs ================= */}
        <section className="section">
          <h2>Quick MCQs</h2>
          <div className="mcq-grid">
            {mcqs.map((m, i) => (
              <div key={i} className="mcq-card">
                <p><strong>Q.</strong> {m.q}</p>
                <p className="answer">Answer: {m.a}</p>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* ================= RIGHT ================= */}
      <aside className="gk-sidebar right">
        <input
          type="text"
          className="gk-search"
          placeholder="Search current affairs..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </aside>
    </div>
  );
};

export default Home;
