import React, { useState } from 'react';
import './MCQCard.css';

const MCQCard = ({ mcq, onAnswer, showAnswer = false, selectedAnswer = null }) => {
  const [selected, setSelected] = useState(selectedAnswer);

  const handleOptionClick = (index) => {
    if (!showAnswer && selected === null) {
      setSelected(index);
      if (onAnswer) {
        onAnswer(mcq._id, index);
      }
    }
  };

  const getOptionClass = (index) => {
    if (!showAnswer && selected === null) return 'option';
    if (!showAnswer && selected === index) return 'option selected';
    if (showAnswer && index === mcq.correctAnswer) return 'option correct';
    if (showAnswer && selected === index && index !== mcq.correctAnswer) return 'option wrong';
    return 'option';
  };

  return (
    <div className="mcq-card">
      <div className="mcq-header">
        <span className="mcq-category">{mcq.category}</span>
        <span className={`mcq-difficulty ${mcq.difficulty?.toLowerCase()}`}>
          {mcq.difficulty}
        </span>
      </div>
      
      <h3 className="mcq-question">{mcq.question}</h3>
      
      <div className="mcq-options">
        {mcq.options?.map((option, index) => (
          <div
            key={index}
            className={getOptionClass(index)}
            onClick={() => handleOptionClick(index)}
          >
            <span className="option-label">{String.fromCharCode(65 + index)}.</span>
            <span className="option-text">{option}</span>
            {showAnswer && index === mcq.correctAnswer && (
              <span className="option-icon correct-icon">âœ“</span>
            )}
            {showAnswer && selected === index && index !== mcq.correctAnswer && (
              <span className="option-icon wrong-icon">âœ—</span>
            )}
          </div>
        ))}
      </div>

      {showAnswer && mcq.explanation && (
        <div className="mcq-explanation">
          <h4>ðŸ’¡ Explanation:</h4>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
};

export default MCQCard;
