import React, { memo, useState } from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import ArticleCard from "../components/ArticleCard";
import {
  FiBookOpen,
  FiAward,
  FiTrendingUp,
  FiTarget,
} from "react-icons/fi";
import "./Home.css";
import "./GKTodayLayout.css";

const Home = () => {
  const [search, setSearch] = useState("");

  /* ---------- QUERIES ---------- */
  const {
    data: latestArticles,
    isLoading: latestLoading,
    isError: latestError,
  } = useQuery(["articles", "latest"], () => articlesAPI.getLatest(6), {
    staleTime: 5 * 60 * 1000,
    retry: 1,
  });

  const {
    data: trendingArticles,
    isLoading: trendingLoading,
    isError: trendingError,
  } = useQuery(["articles", "trending"], () => articlesAPI.getTrending(3), {
    staleTime: 10 * 60 * 1000,
    retry: 1,
  });

  const latestData =
    latestArticles?.data?.filter(
      (a) =>
        a.title.toLowerCase().includes(search.toLowerCase()) ||
        a.content?.toLowerCase().includes(search.toLowerCase())
    ) ?? [];

  const trendingData = trendingArticles?.data ?? [];

  return (
    <div className="gk-layout">
      {/* ================= LEFT SIDEBAR ================= */}
      <aside className="gk-sidebar left">
        <h3>Current Affairs</h3>
        <ul>
          <li>Daily MCQs</li>
          <li>Daily CA Quiz</li>
          <li>Monthly MCQs</li>
          <li>Government Schemes</li>
          <li>Economy & Banking</li>
          <li>Environment</li>
          <li>Awards & Honours</li>
        </ul>
      </aside>

      {/* ================= MAIN CONTENT ================= */}
      <main className="gk-content">
        {/* HERO */}
        <section className="hero-section">
          <h1 className="hero-title">
            Master Current Affairs for <br />
            <span className="gradient-text">Competitive Exams</span>
          </h1>

          <p className="hero-subtitle">
            Daily current affairs, MCQs, and exam-focused content for UPSC,
            SSC, Banking, Railway, and State PSC exams.
          </p>

          <div className="hero-buttons">
            <Link to="/current-affairs" className="btn btn-primary">
              <FiBookOpen /> Explore Current Affairs
            </Link>
            <Link to="/daily-quiz" className="btn btn-outline">
              <FiTarget /> Take Daily Quiz
            </Link>
          </div>

          <div className="hero-stats">
            <Stat number="10K+" label="Daily Visitors" />
            <Stat number="5K+" label="MCQs" />
            <Stat number="100%" label="Free Access" />
          </div>
        </section>

        {/* LATEST ARTICLES */}
        <section className="section">
          <div className="section-header">
            <h2>Latest Current Affairs</h2>
            <Link to="/current-affairs">
              View All <FiTrendingUp />
            </Link>
          </div>

          {latestLoading && <ArticleSkeletonGrid />}
          {latestError && (
            <ErrorBox message="Failed to load latest articles." />
          )}

          {!latestLoading && latestData.length === 0 && (
            <EmptyState message="No matching articles found." />
          )}

          <div className="articles-grid">
            {latestData.map((article) => (
              <ArticleCard key={article._id} article={article} />
            ))}
          </div>
        </section>

        {/* TRENDING */}
        {!trendingLoading &&
          !trendingError &&
          trendingData.length > 0 && (
            <section className="section">
              <h2>
                <FiTrendingUp /> Trending This Week
              </h2>

              <div className="trending-grid">
                {trendingData.map((article, index) => (
                  <Link
                    to={`/article/${article.slug}`}
                    key={article._id}
                    className="trending-item"
                  >
                    <span className="trending-number">#{index + 1}</span>
                    <div>
                      <h3>{article.title}</h3>
                      <span>
                        {article.viewCount ?? 0} views â€¢ {article.category}
                      </span>
                    </div>
                  </Link>
                ))}
              </div>
            </section>
          )}
      </main>

      {/* ================= RIGHT SIDEBAR ================= */}
      <aside className="gk-sidebar right">
        <input
          type="text"
          className="gk-search"
          placeholder="Search current affairs..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <h3>E-Books</h3>
        <ul>
          <li>Current Affairs Monthly MCQs</li>
          <li>CA Articles + MCQs</li>
          <li>Yearly Current Affairs PDF</li>
        </ul>

        <h3>States PSC</h3>
        <ul>
          <li>UPSC</li>
          <li>GPSC</li>
          <li>BPSC</li>
          <li>MPPSC</li>
        </ul>
      </aside>
    </div>
  );
};

export default Home;

/* ================= SUB COMPONENTS ================= */

const Stat = memo(({ number, label }) => (
  <div className="stat-item">
    <div className="stat-number">{number}</div>
    <div className="stat-label">{label}</div>
  </div>
));

const ArticleSkeletonGrid = () => (
  <div className="articles-grid">
    {Array.from({ length: 6 }).map((_, i) => (
      <div key={i} className="article-skeleton" />
    ))}
  </div>
);

const ErrorBox = ({ message }) => (
  <div className="error-box">{message}</div>
);

const EmptyState = ({ message }) => (
  <div className="empty-state">{message}</div>
);
