import React, { memo, useState } from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import ArticleCard from "../components/ArticleCard";
import {
  FiBookOpen,
  FiAward,
  FiTrendingUp,
  FiTarget,
} from "react-icons/fi";
import "./Home.css";
import "./GKTodayLayout.css";

/* ================= DATE HELPERS ================= */

const isToday = (date) => {
  const d = new Date(date);
  const t = new Date();
  return d.toDateString() === t.toDateString();
};

const isYesterday = (date) => {
  const d = new Date(date);
  const y = new Date();
  y.setDate(y.getDate() - 1);
  return d.toDateString() === y.toDateString();
};

/* ================= HOME ================= */

const Home = () => {
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("All");

  /* ---------- QUERIES ---------- */
  const {
    data: latestArticles,
    isLoading: latestLoading,
    isError: latestError,
  } = useQuery(["articles", "latest"], () => articlesAPI.getLatest(10), {
    staleTime: 5 * 60 * 1000,
    retry: 1,
  });

  const {
    data: trendingArticles,
    isLoading: trendingLoading,
    isError: trendingError,
  } = useQuery(["articles", "trending"], () => articlesAPI.getTrending(3), {
    staleTime: 10 * 60 * 1000,
    retry: 1,
  });

  /* ---------- FILTERING ---------- */
  const filtered =
    latestArticles?.data?.filter((a) => {
      const matchesSearch =
        a.title.toLowerCase().includes(search.toLowerCase()) ||
        a.content?.toLowerCase().includes(search.toLowerCase());

      const matchesCategory =
        category === "All" || a.category === category;

      return matchesSearch && matchesCategory;
    }) ?? [];

  const todayArticles = filtered.filter((a) => isToday(a.date));
  const yesterdayArticles = filtered.filter((a) =>
    isYesterday(a.date)
  );
  const earlierArticles = filtered.filter(
    (a) => !isToday(a.date) && !isYesterday(a.date)
  );

  /* ---------- PERSONALIZED RECOMMENDATIONS ---------- */
  const preferredCategory = localStorage.getItem("preferredCategory");

  const recommended =
    preferredCategory && latestArticles?.data
      ? latestArticles.data
          .filter((a) => a.category === preferredCategory)
          .slice(0, 3)
      : [];

  /* ---------- MCQs ---------- */
  const mcqs = [
    {
      q: "Who presents the Union Budget in India?",
      a: "Finance Minister",
    },
    {
      q: "Which mission promotes AI development in India?",
      a: "National AI Mission",
    },
    {
      q: "Which body controls monetary policy in India?",
      a: "Reserve Bank of India",
    },
  ];

  return (
    <div className="gk-layout">
      {/* ================= LEFT SIDEBAR ================= */}
      <aside className="gk-sidebar left">
        <h3>Categories</h3>
        <ul>
          {[
            "All",
            "Economy",
            "Polity",
            "Technology",
            "Science",
            "Environment",
            "International",
          ].map((cat) => (
            <li
              key={cat}
              onClick={() => setCategory(cat)}
              style={{
                fontWeight: category === cat ? "600" : "400",
                color: category === cat ? "#2563eb" : "inherit",
                cursor: "pointer",
              }}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      {/* ================= MAIN CONTENT ================= */}
      <main className="gk-content">
        {/* HERO */}
        <section className="hero-section">
          <h1 className="hero-title">
            Master Current Affairs for <br />
            <span className="gradient-text">Competitive Exams</span>
          </h1>

          <p className="hero-subtitle">
            Daily current affairs, MCQs, and exam-focused content for UPSC,
            SSC, Banking, Railway, and State PSC exams.
          </p>

          <div className="hero-buttons">
            <Link to="/current-affairs" className="btn btn-primary">
              <FiBookOpen /> Explore Current Affairs
            </Link>
            <Link to="/daily-quiz" className="btn btn-outline">
              <FiTarget /> Take Daily Quiz
            </Link>
          </div>
        </section>

        {/* ================= LATEST ================= */}
        <section className="section">
          <h2>Latest Current Affairs</h2>

          {latestLoading && <ArticleSkeletonGrid />}
          {latestError && (
            <ErrorBox message="Failed to load latest articles." />
          )}

          {todayArticles.length > 0 && (
            <>
              <h3 className="group-title">Today</h3>
              <div className="articles-grid">
                {todayArticles.map((a) => (
                  <ArticleCard key={a._id} article={a} />
                ))}
              </div>
            </>
          )}

          {yesterdayArticles.length > 0 && (
            <>
              <h3 className="group-title">Yesterday</h3>
              <div className="articles-grid">
                {yesterdayArticles.map((a) => (
                  <ArticleCard key={a._id} article={a} />
                ))}
              </div>
            </>
          )}

          {earlierArticles.length > 0 && (
            <>
              <h3 className="group-title">Earlier</h3>
              <div className="articles-grid">
                {earlierArticles.map((a) => (
                  <ArticleCard key={a._id} article={a} />
                ))}
              </div>
            </>
          )}

          {!latestLoading && filtered.length === 0 && (
            <EmptyState message="No matching articles found." />
          )}
        </section>

        {/* ================= TRENDING ================= */}
        {!trendingLoading &&
          !trendingError &&
          trendingArticles?.data?.length > 0 && (
            <section className="section">
              <h2>
                <FiTrendingUp /> Trending This Week
              </h2>

              <div className="trending-grid">
                {trendingArticles.data.map((a, i) => (
                  <Link
                    key={a._id}
                    to={`/article/${a.slug}`}
                    className="trending-item"
                  >
                    <span className="trending-number">#{i + 1}</span>
                    <div>
                      <h3>{a.title}</h3>
                      <span>
                        {a.viewCount ?? 0} views â€¢ {a.category}
                      </span>
                    </div>
                  </Link>
                ))}
              </div>
            </section>
          )}

        {/* ================= MCQs ================= */}
        <section className="section">
          <h2>Quick MCQs</h2>

          <div className="mcq-grid">
            {mcqs.map((m, i) => (
              <div key={i} className="mcq-card">
                <p>
                  <strong>Q.</strong> {m.q}
                </p>
                <p className="answer">Answer: {m.a}</p>
              </div>
            ))}
          </div>
        </section>

        {/* ================= RECOMMENDED ================= */}
        {preferredCategory && recommended.length > 0 && (
          <section className="section">
            <h2>Recommended for You</h2>

            <div className="articles-grid">
              {recommended.map((a) => (
                <ArticleCard key={a._id} article={a} />
              ))}
            </div>
          </section>
        )}
      </main>

      {/* ================= RIGHT SIDEBAR ================= */}
      <aside className="gk-sidebar right">
        <input
          type="text"
          className="gk-search"
          placeholder="Search current affairs..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <h3>E-Books</h3>
        <ul>
          <li>Current Affairs Monthly MCQs</li>
          <li>CA Articles + MCQs</li>
          <li>Yearly Current Affairs PDF</li>
        </ul>

        <h3>States PSC</h3>
        <ul>
          <li>UPSC</li>
          <li>GPSC</li>
          <li>BPSC</li>
          <li>MPPSC</li>
        </ul>
      </aside>
    </div>
  );
};

