import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { articlesAPI } from '../services/api';
import ArticleCard from '../components/ArticleCard';
import CategoryFilter from '../components/CategoryFilter';
import SearchBar from '../components/SearchBar';
import { FiFilter } from 'react-icons/fi';
import './CurrentAffairs.css';

const CurrentAffairs = () => {
  const [page, setPage] = useState(1);
  const [category, setCategory] = useState('');
  const [examType, setExamType] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const { data, isLoading, error } = useQuery(
    ['articles', page, category, examType, searchTerm],
    () => articlesAPI.getAll({
      page,
      limit: 12,
      category,
      examRelevance: examType,
      search: searchTerm
    }),
    { 
      keepPreviousData: true,
      retry: 1,
      onError: (err) => {
        console.error('Error loading articles:', err);
      }
    }
  );

  const handleSearch = (term) => {
    setSearchTerm(term);
    setPage(1);
  };

  const handleCategoryChange = (cat) => {
    setCategory(cat);
    setPage(1);
  };

  const handleExamChange = (e) => {
    setExamType(e.target.value);
    setPage(1);
  };

  return (
    <div className="current-affairs-page">
      <div className="page-header">
        <div className="container">
          <h1 className="page-title">Daily Current Affairs</h1>
          <p className="page-subtitle">
            Stay updated with the latest news and events for competitive exams
          </p>
        </div>
      </div>

      <div className="container">
        {/* Search Bar */}
        <SearchBar onSearch={handleSearch} placeholder="Search current affairs..." />

        {/* Filters */}
        <div className="filters-section">
          <CategoryFilter 
            selectedCategory={category} 
            onCategoryChange={handleCategoryChange}
          />

          <div className="exam-filter">
            <label htmlFor="examType">
              <FiFilter /> Filter by Exam:
            </label>
            <select 
              id="examType" 
              value={examType} 
              onChange={handleExamChange}
              className="exam-select"
            >
              <option value="">All Exams</option>
              <option value="UPSC">UPSC</option>
              <option value="SSC">SSC</option>
              <option value="Banking">Banking</option>
              <option value="Railway">Railway</option>
              <option value="State PSC">State PSC</option>
              <option value="BPSC">BPSC</option>
              <option value="UPPSC">UPPSC</option>
              <option value="MPPSC">MPPSC</option>
            </select>
          </div>
        </div>

        {/* Articles Grid */}
        {isLoading ? (
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading articles...</p>
          </div>
        ) : error ? (
          <div className="error-message">
            <p>Error loading articles. Please ensure the backend is running.</p>
            <p className="error-details">{error?.message || 'Connection failed'}</p>
            <button className="btn btn-primary" onClick={() => window.location.reload()}>
              Retry
            </button>
          </div>
        ) : data?.data?.length > 0 ? (
          <>
            <div className="articles-grid">
              {data.data.map((article) => (
                <ArticleCard key={article._id} article={article} />
              ))}
            </div>

            {/* Pagination */}
            {data.pagination && data.pagination.totalPages > 1 && (
              <div className="pagination">
                <button
                  className="pagination-btn"
                  onClick={() => setPage(p => Math.max(1, p - 1))}
                  disabled={page === 1}
                >
                  Previous
                </button>
                
                <div className="pagination-info">
                  Page {page} of {data.pagination.totalPages}
                </div>

                <button
                  className="pagination-btn"
                  onClick={() => setPage(p => Math.min(data.pagination.totalPages, p + 1))}
                  disabled={page === data.pagination.totalPages}
                >
                  Next
                </button>
              </div>
            )}
          </>
        ) : (
          <div className="empty-state">
            <h3>No articles found</h3>
            <p>Try adjusting your filters or search term</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CurrentAffairs;
