import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useQuery } from "react-query";
import { articlesAPI } from "../services/api";
import { useAuth } from "../context/AuthContext";
import { toast } from "react-toastify";
import { format, isValid, parseISO } from "date-fns";
import {
  FiCalendar,
  FiUser,
  FiTag,
  FiBookmark,
  FiHeart,
  FiShare2,
  FiArrowLeft,
  FiEye,
} from "react-icons/fi";
import "./ArticleDetail.css";

const ArticleDetail = () => {
  const { slug } = useParams();
  const { user, isAuthenticated } = useAuth();
  const [isSaved, setIsSaved] = useState(false);

  const { data, isLoading, error } = useQuery(
    ["article", slug],
    async () => {
      const res = await articlesAPI.getBySlug(slug);
      return res?.data || {};
    },
    { retry: 1 }
  );

  const article = data?.data;

  const formatDate = (d) => {
    if (!d) return "Date not available";
    const date = typeof d === "string" ? parseISO(d) : new Date(d);
    return isValid(date) ? format(date, "MMMM dd, yyyy") : "Invalid date";
  };

  const handleShare = () => {
    if (!article) return;

    if (navigator.share) {
      navigator.share({
        title: article.title,
        text: article.summary,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      toast.success("Link copied to clipboard!");
    }
  };

  if (isLoading) {
    return <p>Loading article...</p>;
  }

  if (error || !article) {
    return (
      <div className="error-container">
        <h2>Article not found</h2>
        <Link to="/current-affairs">
          <FiArrowLeft /> Back
        </Link>
      </div>
    );
  }

  return (
    <div className="article-detail-page">
      <div className="container">
        <Link to="/current-affairs" className="back-link">
          <FiArrowLeft /> Back
        </Link>

        <article>
          <h1>{article.title}</h1>

          <div className="article-meta">
            <span>
              <FiCalendar /> {formatDate(article.publishDate)}
            </span>
            <span>
              <FiUser /> {article.author?.name || "Admin"}
            </span>
            <span>
              <FiEye /> {article.viewCount || 0}
            </span>
          </div>

          <div
            className="article-body"
            dangerouslySetInnerHTML={{ __html: article.content }}
          />

          <div className="article-actions">
            <button onClick={handleShare}>
              <FiShare2 /> Share
            </button>
          </div>
        </article>
      </div>
    </div>
  );
};

export default ArticleDetail;
