import React from "react";
import CurrentAffairsItem from "../components/CurrentAffairsItem";
import "../styles/gktoday.css";

const dummyData = [
  {
    title: "DST Scientists Develop Photo-Rechargeable Supercapacitor",
    summary:
      "Indian scientists have developed a solar-powered energy storage device that can capture and store energy within a single unit.",
    date: "February 2, 2026",
    image:
      "https://www.gktoday.in/wp-content/uploads/2026/02/supercapacitor.jpg",
  },
  {
    title: "India AI Impact Summit 2026 to Be Largest Global AI Gathering",
    summary:
      "India AI Impact Summit 2026 will be held in New Delhi and is expected to become the largest AI summit globally.",
    date: "February 2, 2026",
    image:
      "https://www.gktoday.in/wp-content/uploads/2026/02/ai-summit.jpg",
  },
];

const CurrentAffairs = () => {
  return (
    <div className="gk-container">
      <div className="gk-main">
        <h1 className="gk-heading">NMAI Current Affairs
</h1>

        {dummyData.map((item, index) => (
          <CurrentAffairsItem key={index} item={item} />
        ))}

        <div className="older-posts">Older Posts â†’</div>
      </div>

      {/* RIGHT SIDEBAR */}
      <aside className="gk-sidebar">
        <div className="sidebar-box">
          <h3>E-Books</h3>
          <ul>
            <li>Current Affairs Monthly MCQs</li>
            <li>CA Articles + MCQs PDF</li>
            <li>Yearly Current Affairs PDF</li>
          </ul>
        </div>
      </aside>
    </div>
  );
};

export default CurrentAffairs;
