import React, { useState } from "react";
import { useQuery, useMutation } from "react-query";
import { mcqsAPI } from "../services/api";
import MCQCard from "../components/MCQCard";
import { toast } from "react-toastify";
import { FiTarget, FiAward, FiRefreshCw } from "react-icons/fi";
import "./DailyQuiz.css";

const DailyQuiz = () => {
  const [quizStarted, setQuizStarted] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState(null);

  /* ================= FETCH QUIZ ================= */
  const { data: quizData, isLoading, isError } = useQuery(
    ["dailyQuiz"],
    mcqsAPI.getDailyQuiz,
    { enabled: quizStarted }
  );

  const questions = quizData?.data || [];

  /* ================= SUBMIT ================= */
  const submitMutation = useMutation(mcqsAPI.submit, {
    onSuccess: (res) => {
      setResults(res.data);
      setShowResults(true);
      toast.success(`Quiz completed! Score: ${res.data.score}%`);
    },
    onError: () => toast.error("Failed to submit quiz"),
  });

  /* ================= HANDLERS ================= */

  const startQuiz = () => {
    setQuizStarted(true);
    setCurrentQuestion(0);
    setAnswers([]);
    setShowResults(false);
    setResults(null);
  };

  const handleAnswer = (mcqId, selectedAnswer) => {
    const updated = [...answers];
    updated[currentQuestion] = { mcqId, selectedAnswer };
    setAnswers(updated);
  };

  const handleNext = () => {
    const currentAnswer = answers[currentQuestion]?.selectedAnswer;

    if (!currentAnswer) {
      toast.error("Please select an answer");
      return;
    }

    setCurrentQuestion((q) => q + 1);
  };

  const handlePrevious = () => {
    setCurrentQuestion((q) => Math.max(q - 1, 0));
  };

  const handleJump = (index) => {
    if (index > currentQuestion && !answers[currentQuestion]?.selectedAnswer) {
      toast.error("Please answer current question first");
      return;
    }
    setCurrentQuestion(index);
  };

  const handleSubmit = () => {
    if (answers.length !== questions.length) {
      toast.error("Please answer all questions");
      return;
    }
    submitMutation.mutate({ answers });
  };

  const restartQuiz = () => {
    setQuizStarted(false);
    setCurrentQuestion(0);
    setAnswers([]);
    setShowResults(false);
    setResults(null);
  };

  /* ================= START SCREEN ================= */
  if (!quizStarted) {
    return (
      <div className="daily-quiz-page">
        <div className="quiz-start-screen">
          <div className="start-card">
            <FiTarget className="start-icon" />
            <h1>Daily Quiz Challenge</h1>
            <p>Test your knowledge with carefully selected questions</p>

            <div className="quiz-info">
              <div className="info-item">
                <strong>10</strong>
                <span>Questions</span>
              </div>
              <div className="info-item">
                <strong>Mixed</strong>
                <span>Categories</span>
              </div>
              <div className="info-item">
                <strong>10 mins</strong>
                <span>Suggested</span>
              </div>
            </div>

            <button className="btn btn-primary btn-lg" onClick={startQuiz}>
              Start Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* ================= LOADING ================= */
  if (isLoading) {
    return <p style={{ textAlign: "center" }}>Preparing your quiz...</p>;
  }

  /* ================= ERROR ================= */
  if (isError || questions.length === 0) {
    return <p style={{ textAlign: "center" }}>No quiz available today.</p>;
  }

  /* ================= RESULTS ================= */
  if (showResults && results) {
    return (
      <div className="daily-quiz-page">
        <div className="quiz-results-screen">
          <div className="results-card">
            <FiAward className="results-icon" />
            <h1>Quiz Completed!</h1>

            <div className="score-circle">
              <div className="score-value">{results.score}%</div>
              <div className="score-label">Your Score</div>
            </div>

            <button className="btn btn-primary btn-lg" onClick={restartQuiz}>
              <FiRefreshCw /> Take Another Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* ================= QUIZ UI ================= */

  const currentMCQ = questions[currentQuestion];
  const progress = ((currentQuestion + 1) / questions.length) * 100;

  return (
    <div className="daily-quiz-page">
      <div className="quiz-container">
        <div className="quiz-header">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }} />
          </div>
          <div className="progress-text">
            Question {currentQuestion + 1} of {questions.length}
          </div>
        </div>

        <MCQCard
          mcq={currentMCQ}
          onAnswer={handleAnswer}
          selectedAnswer={answers[currentQuestion]?.selectedAnswer}
        />

        <div className="quiz-navigation">
          <button
            className="btn btn-secondary"
            onClick={handlePrevious}
            disabled={currentQuestion === 0}
          >
            Previous
          </button>

          <div className="answer-indicators">
            {questions.map((_, i) => (
              <div
                key={i}
                className={`indicator ${
                  answers[i] ? "answered" : ""
                } ${i === currentQuestion ? "active" : ""}`}
                onClick={() => handleJump(i)}
              >
                {i + 1}
              </div>
            ))}
          </div>

          {currentQuestion < questions.length - 1 ? (
            <button className="btn btn-primary" onClick={handleNext}>
              Next
            </button>
          ) : (
            <button className="btn btn-primary" onClick={handleSubmit}>
              Submit Quiz
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default DailyQuiz;
