// ArticleDetail.jsx - UPDATED
import React, { useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import { articlesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-toastify';
import { format, isValid, parseISO } from 'date-fns';
import { FiCalendar, FiUser, FiTag, FiBookmark, FiHeart, FiShare2, FiArrowLeft, FiEye } from 'react-icons/fi';
import './ArticleDetail.css';

const ArticleDetail = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  const [isSaved, setIsSaved] = useState(false);

  // Fetch article from API
  const { data, isLoading, error } = useQuery(
    ['article', slug],
    () => articlesAPI.getBySlug(slug),
    {
      retry: 1,
      onSuccess: (response) => {
        if (response.success && response.data) {
          // Check if article is saved (you'll need to implement this)
          if (user?.savedArticles?.some(id => id === response.data._id)) {
            setIsSaved(true);
          }
        }
      },
      onError: (err) => {
        console.error('Error loading article:', err);
        toast.error('Failed to load article');
      }
    }
  );

  // Safely format dates
  const formatDate = (dateString) => {
    if (!dateString) return 'Date not available';
    
    try {
      const date = typeof dateString === 'string' ? parseISO(dateString) : new Date(dateString);
      if (isValid(date)) {
        return format(date, 'MMMM dd, yyyy');
      }
      return 'Invalid date';
    } catch (error) {
      console.error('Date formatting error:', error);
      return 'Date not available';
    }
  };

  const handleSaveArticle = async () => {
    if (!isAuthenticated()) {
      toast.error('Please login to save articles');
      return;
    }

    try {
      // Implement your save article API call
      toast.success(isSaved ? 'Article removed from saved' : 'Article saved!');
      setIsSaved(!isSaved);
    } catch (error) {
      toast.error('Failed to save article');
    }
  };

  const handleLike = async () => {
    if (!isAuthenticated()) {
      toast.error('Please login to like articles');
      return;
    }

    try {
      // Implement your like API call
      toast.success('Article liked!');
    } catch (error) {
      toast.error('Failed to like article');
    }
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: article.title,
        text: article.summary,
        url: window.location.href
      }).catch(err => console.log('Share failed:', err));
    } else {
      navigator.clipboard.writeText(window.location.href);
      toast.success('Link copied to clipboard!');
    }
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading article...</p>
      </div>
    );
  }

  if (error || !data?.success) {
    return (
      <div className="error-container">
        <h2>Article not found</h2>
        <p>The article you're looking for doesn't exist or has been removed.</p>
        <p className="error-details">{error?.message || 'Failed to load article'}</p>
        <Link to="/current-affairs" className="btn btn-primary">
          <FiArrowLeft /> Back to Articles
        </Link>
      </div>
    );
  }

  const article = data.data;

  return (
    <div className="article-detail-page">
      <div className="container">
        <Link to="/current-affairs" className="back-link">
          <FiArrowLeft /> Back to Articles
        </Link>

        <article className="article-content">
          {/* Header */}
          <header className="article-header">
            {article.category && (
              <div className="article-category-badge">{article.category}</div>
            )}
            <h1 className="article-title">{article.title || 'Untitled Article'}</h1>
            
            <div className="article-meta">
              {article.publishDate && (
                <div className="meta-item">
                  <FiCalendar />
                  <span>{formatDate(article.publishDate)}</span>
                </div>
              )}
              <div className="meta-item">
                <FiUser />
                <span>{article.author?.name || article.author || 'Admin'}</span>
              </div>
              <div className="meta-item">
                <FiEye />
                <span>{article.viewCount || 0} views</span>
              </div>
              <div className="meta-item">
                <FiHeart />
                <span>{article.likes || 0} likes</span>
              </div>
              {article.readingTime && (
                <div className="meta-item">
                  <span>⏱️ {article.readingTime}</span>
                </div>
              )}
            </div>

            {article.examRelevance && article.examRelevance.length > 0 && (
              <div className="exam-tags">
                <strong>Relevant for:</strong>
                {article.examRelevance.map((exam, index) => (
                  <span key={index} className="exam-tag">{exam}</span>
                ))}
              </div>
            )}
          </header>

          {/* Featured Image */}
          <div className="featured-image">
            <img 
              src={article.featuredImage?.url || `https://source.unsplash.com/random/800x400?${article.category},india`}
              alt={article.featuredImage?.alt || article.title}
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = `https://source.unsplash.com/random/800x400?${article.category}`;
              }}
            />
          </div>

          {/* Summary */}
          {article.summary && (
            <div className="article-summary">
              <h3>Summary</h3>
              <p>{article.summary}</p>
            </div>
          )}

          {/* Content */}
          {article.content && (
            <div 
              className="article-body"
              dangerouslySetInnerHTML={{ __html: article.content }}
            />
          )}

          {/* Tags */}
          {article.tags && article.tags.length > 0 && (
            <div className="article-tags">
              <strong>Tags:</strong>
              {article.tags.map((tag, index) => (
                <span key={index} className="tag">
                  <FiTag /> {tag}
                </span>
              ))}
            </div>
          )}

          {/* Difficulty Level */}
          {article.difficulty && (
            <div className="difficulty-level">
              <strong>Difficulty:</strong>
              <span className={`difficulty-badge ${article.difficulty.toLowerCase()}`}>
                {article.difficulty}
              </span>
            </div>
          )}

          {/* Sources */}
          {article.sources && article.sources.length > 0 && (
            <div className="article-sources">
              <h4>Sources</h4>
              <ul>
                {article.sources.map((source, index) => (
                  <li key={index}>{source}</li>
                ))}
              </ul>
            </div>
          )}

          {/* Action Buttons */}
          <div className="article-actions">
            <button 
              className={`action-btn ${isSaved ? 'active' : ''}`}
              onClick={handleSaveArticle}
            >
              <FiBookmark /> {isSaved ? 'Saved' : 'Save Article'}
            </button>
            <button className="action-btn" onClick={handleLike}>
              <FiHeart /> Like ({article.likes || 0})
            </button>
            <button className="action-btn" onClick={handleShare}>
              <FiShare2 /> Share
            </button>
          </div>
        </article>

        {/* Related Articles */}
        {article.relatedArticles && article.relatedArticles.length > 0 && (
          <section className="related-articles">
            <h2>Related Articles</h2>
            <div className="related-grid">
              {article.relatedArticles.map((related) => (
                <Link 
                  to={`/article/${related.slug}`} 
                  key={related._id || related.id}
                  className="related-card"
                >
                  <div className="related-image">
                    <img 
                      src={related.featuredImage?.url || `https://source.unsplash.com/random/300x200?${related.category}`}
                      alt={related.title}
                    />
                    <span className="related-category">{related.category}</span>
                  </div>
                  <div className="related-content">
                    <h3>{related.title}</h3>
                    {related.summary && <p>{related.summary.substring(0, 100)}...</p>}
                    <div className="related-meta">
                      <span><FiCalendar /> {formatDate(related.publishDate)}</span>
                      <span><FiEye /> {related.viewCount || 0}</span>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          </section>
        )}
      </div>
    </div>
  );
};

export default ArticleDetail;
