import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useQuery } from "react-query";
import { articlesAPI, usersAPI } from "../services/api";
import { useAuth } from "../context/AuthContext";
import { toast } from "react-toastify";
import { format, isValid, parseISO } from "date-fns";
import {
  FiCalendar,
  FiUser,
  FiTag,
  FiBookmark,
  FiHeart,
  FiShare2,
  FiArrowLeft,
} from "react-icons/fi";
import "./ArticleDetail.css";

const ArticleDetail = () => {
  const { slug } = useParams();
  const { user, isAuthenticated } = useAuth();
  const [isSaved, setIsSaved] = useState(false);

  const { data, isLoading, error } = useQuery(
    ["article", slug],
    () => articlesAPI.getBySlug(slug),
    {
      retry: 1,
      onSuccess: (res) => {
        const article = res?.data?.data;
        if (user?.savedArticles?.includes(article?._id)) {
          setIsSaved(true);
        }
      },
    }
  );

  const article = data?.data?.data;

  const formatDate = (dateString) => {
    if (!dateString) return "Date not available";
    try {
      const date = parseISO(dateString);
      return isValid(date) ? format(date, "MMMM dd, yyyy") : "Invalid date";
    } catch {
      return "Date not available";
    }
  };

  const handleSaveArticle = async () => {
    if (!isAuthenticated()) {
      toast.error("Please login to save articles");
      return;
    }

    try {
      if (isSaved) {
        await usersAPI.unsaveArticle(article._id);
        setIsSaved(false);
        toast.success("Article removed from saved");
      } else {
        await usersAPI.saveArticle(article._id);
        setIsSaved(true);
        toast.success("Article saved!");
      }
    } catch {
      toast.error("Failed to save article");
    }
  };

  const handleLike = async () => {
    if (!isAuthenticated()) {
      toast.error("Please login to like articles");
      return;
    }

    try {
      await articlesAPI.like(article._id);
      toast.success("Article liked!");
    } catch {
      toast.error("Failed to like article");
    }
  };

  const handleShare = () => {
    const url = window.location.href;
    if (navigator.share) {
      navigator.share({
        title: article.title,
        text: article.summary,
        url,
      });
    } else {
      navigator.clipboard.writeText(url);
      toast.success("Link copied to clipboard!");
    }
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading article...</p>
      </div>
    );
  }

  if (error || !article) {
    return (
      <div className="error-container">
        <h2>Article not found</h2>
        <Link to="/current-affairs" className="btn btn-primary">
          Back to Articles
        </Link>
      </div>
    );
  }

  return (
    <div className="article-detail-page">
      <div className="container">
        <Link to="/current-affairs" className="back-link">
          <FiArrowLeft /> Back to Articles
        </Link>

        <article className="article-content">
          <header className="article-header">
            <div className="article-category-badge">
              {article.category}
            </div>

            <h1 className="article-title">{article.title}</h1>

            <div className="article-meta">
              <span><FiCalendar /> {formatDate(article.publishDate)}</span>
              <span><FiUser /> {article.author?.name || "Admin"}</span>
              <span><FiTag /> {article.viewCount} views</span>
            </div>

            {article.examRelevance?.length > 0 && (
              <div className="exam-tags">
                <strong>Relevant for:</strong>
                {article.examRelevance.map((e) => (
                  <span key={e} className="exam-tag">{e}</span>
                ))}
              </div>
            )}
          </header>

          {article.featuredImage?.url && (
            <div className="featured-image">
              <img
                src={article.featuredImage.url}
                alt={article.featuredImage.alt}
              />
            </div>
          )}

          <div className="article-summary">
            <strong>Summary:</strong> {article.summary}
          </div>

          <div
            className="article-body"
            dangerouslySetInnerHTML={{ __html: article.content }}
          />

          {article.tags?.length > 0 && (
            <div className="article-tags">
              {article.tags.map((tag) => (
                <span key={tag} className="tag">{tag}</span>
              ))}
            </div>
          )}

          <div className="article-actions">
            <button
              className={`action-btn ${isSaved ? "active" : ""}`}
              onClick={handleSaveArticle}
            >
              <FiBookmark /> {isSaved ? "Saved" : "Save"}
            </button>

            <button className="action-btn" onClick={handleLike}>
              <FiHeart /> Like ({article.likes})
            </button>

            <button className="action-btn" onClick={handleShare}>
              <FiShare2 /> Share
            </button>
          </div>
        </article>
      </div>
    </div>
  );
};

export default ArticleDetail;
