// ArticleDetail.jsx - FIXED & STABLE
import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useQuery } from "react-query";
import { articlesAPI } from "../services/api";
import { useAuth } from "../context/AuthContext";
import { toast } from "react-toastify";
import { format, isValid, parseISO } from "date-fns";
import {
  FiCalendar,
  FiUser,
  FiTag,
  FiBookmark,
  FiHeart,
  FiShare2,
  FiArrowLeft,
  FiEye,
} from "react-icons/fi";
import "./ArticleDetail.css";

const ArticleDetail = () => {
  const { slug } = useParams();
  const { user } = useAuth(); // ✅ ONLY user
  const [isSaved, setIsSaved] = useState(false);

  /* ================= FETCH ARTICLE ================= */
  const { data, isLoading, error } = useQuery(
    ["article", slug],
    () => articlesAPI.getBySlug(slug),
    {
      retry: 1,
      onSuccess: (response) => {
        if (response?.success && response?.data && user?.savedArticles) {
          if (user.savedArticles.includes(response.data._id)) {
            setIsSaved(true);
          }
        }
      },
      onError: () => {
        toast.error("Failed to load article");
      },
    }
  );

  /* ================= DATE FORMAT ================= */
  const formatDate = (dateString) => {
    if (!dateString) return "Date not available";
    try {
      const date =
        typeof dateString === "string"
          ? parseISO(dateString)
          : new Date(dateString);
      return isValid(date) ? format(date, "MMMM dd, yyyy") : "Invalid date";
    } catch {
      return "Date not available";
    }
  };

  /* ================= ACTIONS ================= */

  const handleSaveArticle = async () => {
    if (!user) {
      toast.error("Please login to save articles");
      return;
    }

    try {
      // TODO: call save / unsave API
      setIsSaved((prev) => !prev);
      toast.success(isSaved ? "Removed from saved" : "Article saved!");
    } catch {
      toast.error("Failed to save article");
    }
  };

  const handleLike = async () => {
    if (!user) {
      toast.error("Please login to like articles");
      return;
    }

    try {
      // TODO: call like API
      toast.success("Article liked!");
    } catch {
      toast.error("Failed to like article");
    }
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: article.title,
        text: article.summary,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      toast.success("Link copied to clipboard!");
    }
  };

  /* ================= STATES ================= */

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner" />
        <p>Loading article...</p>
      </div>
    );
  }

  if (error || !data?.success) {
    return (
      <div className="error-container">
        <h2>Article not found</h2>
        <Link to="/current-affairs" className="btn btn-primary">
          <FiArrowLeft /> Back to Articles
        </Link>
      </div>
    );
  }

  const article = data.data;

  /* ================= UI ================= */

  return (
    <div className="article-detail-page">
      <div className="container">
        <Link to="/current-affairs" className="back-link">
          <FiArrowLeft /> Back to Articles
        </Link>

        <article className="article-content">
          {/* HEADER */}
          <header className="article-header">
            {article.category && (
              <div className="article-category-badge">
                {article.category}
              </div>
            )}

            <h1 className="article-title">{article.title}</h1>

            <div className="article-meta">
              {article.publishDate && (
                <span>
                  <FiCalendar /> {formatDate(article.publishDate)}
                </span>
              )}
              <span>
                <FiUser /> {article.author?.name || "Admin"}
              </span>
              <span>
                <FiEye /> {article.viewCount || 0}
              </span>
              <span>
                <FiHeart /> {article.likes || 0}
              </span>
            </div>

            {article.examRelevance?.length > 0 && (
              <div className="exam-tags">
                <strong>Relevant for:</strong>
                {article.examRelevance.map((exam, i) => (
                  <span key={i} className="exam-tag">
                    {exam}
                  </span>
                ))}
              </div>
            )}
          </header>

          {/* IMAGE */}
          <div className="featured-image">
            <img
              src={
                article.featuredImage?.url ||
                `https://source.unsplash.com/random/800x400?${article.category}`
              }
              alt={article.title}
            />
          </div>

          {/* SUMMARY */}
          {article.summary && (
            <div className="article-summary">
              <h3>Summary</h3>
              <p>{article.summary}</p>
            </div>
          )}

          {/* CONTENT */}
          {article.content && (
            <div
              className="article-body"
              dangerouslySetInnerHTML={{ __html: article.content }}
            />
          )}

          {/* TAGS */}
          {article.tags?.length > 0 && (
            <div className="article-tags">
              <strong>Tags:</strong>
              {article.tags.map((tag, i) => (
                <span key={i} className="tag">
                  <FiTag /> {tag}
                </span>
              ))}
            </div>
          )}

          {/* ACTIONS */}
          <div className="article-actions">
            <button
              className={`action-btn ${isSaved ? "active" : ""}`}
              onClick={handleSaveArticle}
            >
              <FiBookmark /> {isSaved ? "Saved" : "Save Article"}
            </button>

            <button className="action-btn" onClick={handleLike}>
              <FiHeart /> Like
            </button>

            <button className="action-btn" onClick={handleShare}>
              <FiShare2 /> Share
            </button>
          </div>
        </article>
      </div>
    </div>
  );
};

export default ArticleDetail;
