import React, { useState } from "react";
import { useQuery } from "react-query";
import { mcqsAPI } from "../services/api";
import "./MCQPractice.css";

const CATEGORIES = [
  "All",
  "Polity",
  "Economy",
  "History",
  "Geography",
  "Science",
  "Environment",
  "Current Affairs",
];

const DIFFICULTY = ["All", "Easy", "Medium", "Hard"];

const MCQPractice = () => {
  const [page, setPage] = useState(1);
  const [category, setCategory] = useState("All");
  const [examType, setExamType] = useState("");
  const [difficulty, setDifficulty] = useState("All");

  /* ================= QUERY ================= */
  const { data, isLoading, error } = useQuery(
    ["mcqs", page, category, examType, difficulty],
    () => {
      const params = {
        page,
        limit: 10,
      };

      if (category !== "All") params.category = category;
      if (examType) params.examType = examType;
      if (difficulty !== "All") params.difficulty = difficulty;

      return mcqsAPI.getAll(params);
    },
    {
      keepPreviousData: true,
    }
  );

  return (
    <div className="mcq-page">
      {/* ================= HEADER ================= */}
      <div className="mcq-header">
        <h1>MCQ Practice</h1>
        <p>Practice topic-wise MCQs for UPSC, SSC, Banking & State PSC</p>
      </div>

      {/* ================= FILTER BAR ================= */}
      <div className="mcq-filters">
        <select
          value={category}
          onChange={(e) => {
            setCategory(e.target.value);
            setPage(1);
          }}
        >
          {CATEGORIES.map((c) => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>

        <select
          value={examType}
          onChange={(e) => {
            setExamType(e.target.value);
            setPage(1);
          }}
        >
          <option value="">All Exams</option>
          <option value="UPSC">UPSC</option>
          <option value="SSC">SSC</option>
          <option value="Banking">Banking</option>
          <option value="State PSC">State PSC</option>
        </select>

        <select
          value={difficulty}
          onChange={(e) => {
            setDifficulty(e.target.value);
            setPage(1);
          }}
        >
          {DIFFICULTY.map((d) => (
            <option key={d} value={d}>
              {d}
            </option>
          ))}
        </select>
      </div>

      {/* ================= CONTENT ================= */}
      {isLoading ? (
        <p className="mcq-loading">Loading MCQs...</p>
      ) : error ? (
        <p className="mcq-error">
          Failed to load MCQs. Please try again.
        </p>
      ) : data?.data?.length ? (
        <>
          <div className="mcq-list">
            {data.data.map((mcq, index) => (
              <div key={mcq._id} className="mcq-card">
                <p className="mcq-question">
                  <strong>Q{(page - 1) * 10 + index + 1}.</strong>{" "}
                  {mcq.question}
                </p>

                <ul className="mcq-options">
                  {mcq.options.map((opt, i) => (
                    <li key={i}>{opt}</li>
                  ))}
                </ul>

                <div className="mcq-meta">
                  <span>{mcq.category}</span>
                  <span>{mcq.examType}</span>
                  <span>{mcq.difficulty}</span>
                </div>
              </div>
            ))}
          </div>

          {/* ================= PAGINATION ================= */}
          {data.pagination?.totalPages > 1 && (
            <div className="mcq-pagination">
              <button
                disabled={page === 1}
                onClick={() => setPage((p) => p - 1)}
              >
                Previous
              </button>

              <span>
                Page {page} of {data.pagination.totalPages}
              </span>

              <button
                disabled={page === data.pagination.totalPages}
                onClick={() => setPage((p) => p + 1)}
              >
                Next
              </button>
            </div>
          )}
        </>
      ) : (
        <p className="mcq-empty">No MCQs found.</p>
      )}
    </div>
  );
};

export default MCQPractice;
