import React from "react";
import "./MCQCard.css";

const MCQCard = ({
  mcq,
  selectedAnswer,
  onAnswer,
  showAnswer = false, // üîë ONLY true in results screen
}) => {
  if (!mcq) return null;

  const correctIndex = mcq.correctAnswer;

  const handleClick = (index) => {
    if (showAnswer) return; // ‚ùå lock ONLY in review mode
    onAnswer(mcq._id, index);
  };

  const getOptionClass = (index) => {
    if (!showAnswer) {
      return index === selectedAnswer ? "option selected" : "option";
    }

    if (index === correctIndex) return "option correct";
    if (index === selectedAnswer) return "option wrong";
    return "option";
  };

  return (
    <div className="mcq-card">
      {/* HEADER */}
      <div className="mcq-header">
        <span className="mcq-category">{mcq.category}</span>
        {mcq.difficulty && (
          <span className={`mcq-difficulty ${mcq.difficulty.toLowerCase()}`}>
            {mcq.difficulty}
          </span>
        )}
      </div>

      {/* QUESTION */}
      <h3 className="mcq-question">{mcq.question}</h3>

      {/* OPTIONS */}
      <div className="mcq-options">
        {mcq.options.map((option, index) => (
          <div
            key={index}
            className={getOptionClass(index)}
            onClick={() => handleClick(index)}
          >
            <span className="option-label">
              {String.fromCharCode(65 + index)}.
            </span>

            <span className="option-text">{option}</span>

            {showAnswer && index === correctIndex && (
              <span className="option-icon correct-icon">‚úî</span>
            )}

            {showAnswer &&
              index === selectedAnswer &&
              index !== correctIndex && (
                <span className="option-icon wrong-icon">‚úñ</span>
              )}
          </div>
        ))}
      </div>

      {/* EXPLANATION */}
      {showAnswer && (
        <div className="mcq-explanation">
          <strong>
            {selectedAnswer === correctIndex
              ? "‚úÖ Correct"
              : "‚ùå Incorrect"}
          </strong>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
};

export default MCQCard;
