import React from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import "./Home.css";

const Home = () => {
  const { data, isLoading, isError } = useQuery(
    ["articles", "latest", 10],
    () => articlesAPI.getLatest(10),
    { staleTime: 5 * 60 * 1000 }
  );

  const articles = data?.data || [];

  return (
    <div className="gk-layout">
      {/* ================= MAIN CONTENT ================= */}
      <main className="gk-main">
        <h1 className="gk-heading">NMAI â€“ Current Affairs</h1>

        {isLoading && <p>Loading current affairsâ€¦</p>}
        {isError && <p className="error-box">Failed to load articles.</p>}

        {!isLoading &&
          !isError &&
          articles.map((article) => (
            <Link
              key={article._id}
              to={`/article/${article.slug}`}
              className="gk-article"
            >
              <img
                src={
                  article.featuredImage?.url ||
                  "/placeholder.jpg"
                }
                alt={article.title}
                className="gk-article-img"
              />

              <div className="gk-article-body">
                <h2 className="gk-article-title">{article.title}</h2>
                <p className="gk-article-summary">{article.summary}</p>
                <span className="gk-date">
                  ðŸ“… {new Date(article.createdAt).toDateString()}
                </span>
              </div>
            </Link>
          ))}

        <div className="gk-older">Older Posts â†’</div>
      </main>

      {/* ================= SIDEBAR ================= */}
      <aside className="gk-sidebar">
        <div className="sidebar-box">
          <h3>Current Affairs</h3>
          <ul>
            <li>
              <Link to="/daily-quiz">Daily Current Affairs Quiz</Link>
            </li>
            <li>
              <Link to="/mcq-practice">Practice MCQs</Link>
            </li>
            <li>
              <Link to="/current-affairs">All Current Affairs</Link>
            </li>
          </ul>
        </div>

        <div className="sidebar-box">
          <h3>E-Books</h3>
          <ul>
            <li>Monthly Current Affairs PDF</li>
            <li>CA Articles + MCQs</li>
            <li>Yearly Compilation</li>
          </ul>
        </div>
      </aside>
    </div>
  );
};

export default Home;
