import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import localArticles from "../data/articles";
import ArticleCard from "../components/ArticleCard";
import { FiBookOpen, FiTrendingUp, FiTarget } from "react-icons/fi";
import "./Home.css";
import "./GKTodayLayout.css";

/* ================= CATEGORY MAP ================= */
const CATEGORY_MAP = {
  All: null,
  Economy: ["Economy"],
  Polity: ["Polity"],
  Science: ["Science"],
  Technology: ["Technology"],
  Environment: ["Environment"],
  International: ["International"],
  National: ["National", "Social"],
  Defence: ["Defence", "Defense"],
};

/* ================= DATE HELPERS ================= */
const sameDay = (a, b) =>
  new Date(a).toDateString() === new Date(b).toDateString();

const isToday = (d) => d && sameDay(d, new Date());
const isYesterday = (d) => {
  const y = new Date();
  y.setDate(y.getDate() - 1);
  return d && sameDay(d, y);
};

const formatDate = (d) =>
  new Date(d).toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
  });

/* ================= HOME ================= */
const Home = () => {
  const [articles, setArticles] = useState([]);
  const [trending, setTrending] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("All");

  /* ================= FETCH ================= */
  useEffect(() => {
    let mounted = true;

    const fetchData = async () => {
      try {
        const [latestRes, trendingRes] = await Promise.all([
          articlesAPI.getLatest(20),
          articlesAPI.getTrending(5),
        ]);

        if (!mounted) return;

        setArticles(
          Array.isArray(latestRes?.data?.data)
            ? latestRes.data.data
            : []
        );

        setTrending(
          Array.isArray(trendingRes?.data?.data)
            ? trendingRes.data.data
            : []
        );
      } catch {
        setArticles(Array.isArray(localArticles) ? localArticles : []);
        setTrending(localArticles.slice(0, 5));
      } finally {
        if (mounted) setLoading(false);
      }
    };

    fetchData();
    return () => (mounted = false);
  }, []);

  /* ================= FILTER ================= */
  const filtered = articles.filter((a) => {
    if (!a?.title) return false;

    const text = `${a.title} ${a.summary || ""}`.toLowerCase();
    if (!text.includes(search.toLowerCase())) return false;

    if (category === "All") return true;

    return CATEGORY_MAP[category]?.includes(a.category);
  });

  const today = filtered.filter((a) =>
    isToday(a.publishDate || a.createdAt)
  );
  const yesterday = filtered.filter((a) =>
    isYesterday(a.publishDate || a.createdAt)
  );
  const earlier = filtered.filter(
    (a) =>
      !isToday(a.publishDate || a.createdAt) &&
      !isYesterday(a.publishDate || a.createdAt)
  );

  const showLatestFallback =
    today.length === 0 && yesterday.length === 0;

  /* ================= RENDER ================= */
  return (
    <div className="gk-layout">
      {/* LEFT */}
      <aside className="gk-sidebar left">
        <h3>Categories</h3>
        <ul>
          {Object.keys(CATEGORY_MAP).map((cat) => (
            <li
              key={cat}
              onClick={() => setCategory(cat)}
              className={category === cat ? "active" : ""}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      {/* MAIN */}
      <main className="gk-content">
        {/* HERO */}
        <section className="hero-section">
          <h1>
            Master Current Affairs for <br />
            <span className="gradient-text">Competitive Exams</span>
          </h1>
          <p>Daily current affairs, MCQs, and exam-focused content.</p>

          <div className="hero-buttons">
            <Link to="/current-affairs" className="btn btn-primary">
              <FiBookOpen /> Explore
            </Link>
            <Link to="/daily-quiz" className="btn btn-outline">
              <FiTarget /> Daily Quiz
            </Link>
          </div>
        </section>

        {/* ARTICLES */}
        <section className="section">
          <h2>Latest Current Affairs</h2>

          {loading && <p>Loading articlesâ€¦</p>}

          {!loading && today.length > 0 && (
            <>
              <h3 className="group-title">Today</h3>
              <div className="articles-grid">
                {today.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{ ...a, dateLabel: "Today" }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && yesterday.length > 0 && (
            <>
              <h3 className="group-title">Yesterday</h3>
              <div className="articles-grid">
                {yesterday.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{ ...a, dateLabel: "Yesterday" }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && (showLatestFallback || earlier.length > 0) && (
            <>
              <h3 className="group-title">
                {showLatestFallback ? "Latest" : "Earlier"}
              </h3>
              <div className="articles-grid">
                {(showLatestFallback ? filtered : earlier).map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{
                      ...a,
                      dateLabel: formatDate(a.publishDate || a.createdAt),
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && filtered.length === 0 && (
            <div className="empty-state">No matching articles found.</div>
          )}
        </section>

        {/* TRENDING */}
        {trending.length > 0 && (
          <section className="section">
            <h2>
              <FiTrendingUp /> Trending
            </h2>
            <div className="trending-grid">
              {trending.map((a, i) => (
                <Link
                  key={a._id}
                  to={`/article/${a.slug}`}
                  className="trending-item"
                >
                  <span className="trending-number">#{i + 1}</span>
                  <h3>{a.title}</h3>
                </Link>
              ))}
            </div>
          </section>
        )}
      </main>

      {/* RIGHT */}
      <aside className="gk-sidebar right">
        <input
          className="gk-search"
          placeholder="Search current affairs..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </aside>
    </div>
  );
};

export default Home;
