import React, { useEffect, useState } from "react";
import "./MCQCard.css";

const MCQCard = ({ mcq, selectedAnswer, onAnswer }) => {
  const [selected, setSelected] = useState(null);

  // üî• NORMALIZE CORRECT ANSWER (handles 1-based, string, etc.)
  const correctIndex = Number(mcq.correctAnswer) > 3
    ? Number(mcq.correctAnswer) - 1
    : Number(mcq.correctAnswer);

  // sync with parent (important for navigation / review mode)
  useEffect(() => {
    setSelected(selectedAnswer ?? null);
  }, [selectedAnswer]);

  const isAnswered = selected !== null;
  const isCorrect = selected === correctIndex;

  const handleClick = (index) => {
    if (isAnswered) return;
    setSelected(index);
    onAnswer?.(mcq._id, index);
  };

  const getOptionClass = (index) => {
    if (!isAnswered) return "option";

    if (index === correctIndex) return "option correct";

    if (index === selected) return "option wrong";

    return "option";
  };

  return (
    <div className="mcq-card">
      {/* HEADER */}
      <div className="mcq-header">
        <span className="mcq-category">{mcq.category}</span>
        <span className={`mcq-difficulty ${mcq.difficulty?.toLowerCase()}`}>
          {mcq.difficulty}
        </span>
      </div>

      {/* QUESTION */}
      <h3 className="mcq-question">{mcq.question}</h3>

      {/* OPTIONS */}
      <div className="mcq-options">
        {mcq.options.map((option, index) => (
          <div
            key={index}
            className={getOptionClass(index)}
            onClick={() => handleClick(index)}
          >
            <span className="option-label">
              {String.fromCharCode(65 + index)}.
            </span>

            <span className="option-text">{option}</span>

            {isAnswered && index === correctIndex && (
              <span className="option-icon correct-icon">‚úî</span>
            )}

            {isAnswered &&
              index === selected &&
              index !== correctIndex && (
                <span className="option-icon wrong-icon">‚úñ</span>
              )}
          </div>
        ))}
      </div>

      {/* RESULT + EXPLANATION */}
      {isAnswered && (
        <div className="mcq-explanation">
          <strong>{isCorrect ? "‚úÖ Correct" : "‚ùå Incorrect"}</strong>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
};

export default MCQCard;
