import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import localArticles from "../data/articles";
import ArticleCard from "../components/ArticleCard";
import {
  FiBookOpen,
  FiTrendingUp,
  FiTarget,
} from "react-icons/fi";
import "./Home.css";
import "./GKTodayLayout.css";

/* ================= DATE HELPERS (SAFE) ================= */

const safeDate = (d) => {
  const date = new Date(d);
  return isNaN(date.getTime()) ? null : date;
};

const isToday = (createdAt) => {
  const d = safeDate(createdAt);
  if (!d) return false;
  const t = new Date();
  return d.toDateString() === t.toDateString();
};

const isYesterday = (createdAt) => {
  const d = safeDate(createdAt);
  if (!d) return false;
  const y = new Date();
  y.setDate(y.getDate() - 1);
  return d.toDateString() === y.toDateString();
};

/* ================= HOME ================= */

const Home = () => {
  const [articles, setArticles] = useState(localArticles);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("All");

  /* ================= API + FALLBACK ================= */

  useEffect(() => {
    let mounted = true;

    const fetchArticles = async () => {
      try {
        const res = await articlesAPI.getLatest(10);
        if (mounted && res?.data?.length) {
          setArticles(res.data);
        }
      } catch {
        setArticles(localArticles);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    fetchArticles();
    return () => (mounted = false);
  }, []);

  /* ================= FILTERING ================= */

  const filtered = articles.filter((a) => {
    const textMatch =
      a.title?.toLowerCase().includes(search.toLowerCase()) ||
      a.content?.toLowerCase().includes(search.toLowerCase());

    const categoryMatch =
      category === "All" || a.category === category;

    return textMatch && categoryMatch;
  });

  const todayArticles = filtered.filter((a) =>
    isToday(a.createdAt)
  );
  const yesterdayArticles = filtered.filter((a) =>
    isYesterday(a.createdAt)
  );
  const earlierArticles = filtered.filter(
    (a) =>
      !isToday(a.createdAt) &&
      !isYesterday(a.createdAt)
  );

  const hasDateGroups =
    todayArticles.length ||
    yesterdayArticles.length ||
    earlierArticles.length;

  /* ================= TRENDING ================= */

  const trendingArticles = articles.slice(0, 3);

  /* ================= MCQs ================= */

  const mcqs = [
    { q: "Who presents the Union Budget in India?", a: "Finance Minister" },
    { q: "Which mission promotes AI in India?", a: "National AI Mission" },
    { q: "Who controls monetary policy in India?", a: "RBI" },
  ];

  return (
    <div className="gk-layout">
      {/* LEFT SIDEBAR */}
      <aside className="gk-sidebar left">
        <h3>Categories</h3>
        <ul>
          {[
            "All",
            "Economy",
            "Polity",
            "Technology",
            "Science",
            "Environment",
            "International",
          ].map((cat) => (
            <li
              key={cat}
              onClick={() => setCategory(cat)}
              style={{
                fontWeight: category === cat ? 600 : 400,
                color: category === cat ? "#2563eb" : "inherit",
                cursor: "pointer",
              }}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      {/* MAIN */}
      <main className="gk-content">
        {/* HERO */}
        <section className="hero-section">
          <h1 className="hero-title">
            Master Current Affairs for <br />
            <span className="gradient-text">Competitive Exams</span>
          </h1>
          <p className="hero-subtitle">
            Daily current affairs, MCQs, and exam-focused content.
          </p>
          <div className="hero-buttons">
            <Link to="/current-affairs" className="btn btn-primary">
              <FiBookOpen /> Explore
            </Link>
            <Link to="/daily-quiz" className="btn btn-outline">
              <FiTarget /> Quiz
            </Link>
          </div>
        </section>

        {/* LATEST */}
        <section className="section">
          <h2>Latest Current Affairs</h2>

          {loading && <p>Loading articlesâ€¦</p>}

          {!loading && todayArticles.length > 0 && (
            <>
              <h3 className="group-title">Today</h3>
              <div className="articles-grid">
                {todayArticles.map((a) => (
                  <ArticleCard key={a._id || a.id} article={a} />
                ))}
              </div>
            </>
          )}

          {!loading && yesterdayArticles.length > 0 && (
            <>
              <h3 className="group-title">Yesterday</h3>
              <div className="articles-grid">
                {yesterdayArticles.map((a) => (
                  <ArticleCard key={a._id || a.id} article={a} />
                ))}
              </div>
            </>
          )}

          {!loading && earlierArticles.length > 0 && (
            <>
              <h3 className="group-title">Earlier</h3>
              <div className="articles-grid">
                {earlierArticles.map((a) => (
                  <ArticleCard key={a._id || a.id} article={a} />
                ))}
              </div>
            </>
          )}

          {/* ðŸ”¥ FINAL FALLBACK (CRITICAL FIX) */}
          {!loading && filtered.length > 0 && !hasDateGroups && (
            <div className="articles-grid">
              {filtered.map((a) => (
                <ArticleCard key={a._id || a.id} article={a} />
              ))}
            </div>
          )}

          {!loading && filtered.length === 0 && (
            <div className="empty-state">No matching articles found.</div>
          )}
        </section>

        {/* TRENDING */}
        <section className="section">
          <h2>
            <FiTrendingUp /> Trending This Week
          </h2>
          <div className="trending-grid">
            {trendingArticles.map((a, i) => (
              <Link
                key={a._id || a.id}
                to={`/article/${a.slug}`}
                className="trending-item"
              >
                <span className="trending-number">#{i + 1}</span>
                <div>
                  <h3>{a.title}</h3>
                  <span>{a.category}</span>
                </div>
              </Link>
            ))}
          </div>
        </section>

        {/* MCQs */}
        <section className="section">
          <h2>Quick MCQs</h2>
          <div className="mcq-grid">
            {mcqs.map((m, i) => (
              <div key={i} className="mcq-card">
                <p><strong>Q.</strong> {m.q}</p>
                <p className="answer">Answer: {m.a}</p>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* RIGHT SIDEBAR */}
      <aside className="gk-sidebar right">
        <input
          type="text"
          className="gk-search"
          placeholder="Search current affairs..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </aside>
    </div>
  );
};

export default Home;
