import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { articlesAPI } from "../services/api";
import ArticleCard from "../components/ArticleCard";
import { FiBookOpen, FiTrendingUp, FiTarget } from "react-icons/fi";
import "./Home.css";
import "./GKTodayLayout.css";

/* ================= DATE HELPERS ================= */

const isSameDay = (a, b) =>
  new Date(a).toDateString() === new Date(b).toDateString();

const isToday = (date) =>
  date && isSameDay(date, new Date());

const isYesterday = (date) => {
  if (!date) return false;
  const y = new Date();
  y.setDate(y.getDate() - 1);
  return isSameDay(date, y);
};

const formatDate = (date) =>
  new Date(date).toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
  });

const getDateLabel = (date) => {
  if (isToday(date)) return "Today";
  if (isYesterday(date)) return "Yesterday";
  return formatDate(date);
};

/* ================= HOME ================= */

const Home = () => {
  const [articles, setArticles] = useState([]);
  const [trending, setTrending] = useState([]);
  const [loading, setLoading] = useState(true);

  /* ================= FETCH ================= */
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const [latestRes, trendingRes] = await Promise.all([
          articlesAPI.getLatest(20),
          articlesAPI.getTrending(5),
        ]);

        setArticles(
          Array.isArray(latestRes.data.data)
            ? latestRes.data.data
            : []
        );

        setTrending(
          Array.isArray(trendingRes.data.data)
            ? trendingRes.data.data
            : []
        );
      } catch (err) {
        console.error("Failed to load articles", err);
      } finally {
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  /* ================= GROUP ================= */
  const today = articles.filter((a) =>
    isToday(a.publishDate)
  );

  const yesterday = articles.filter((a) =>
    isYesterday(a.publishDate)
  );

  const earlier = articles.filter(
    (a) =>
      !isToday(a.publishDate) &&
      !isYesterday(a.publishDate)
  );

  return (
    <div className="gk-layout">
      {/* ================= MAIN ================= */}
      <main className="gk-content">
        {/* HERO */}
        <section className="hero-section">
          <h1 className="hero-title">
            Master Current Affairs for <br />
            <span className="gradient-text">
              Competitive Exams
            </span>
          </h1>

          <p className="hero-subtitle">
            Daily current affairs for UPSC, SSC, Banking &
            State PSC exams.
          </p>

          <div className="hero-buttons">
            <Link to="/current-affairs" className="btn btn-primary">
              <FiBookOpen /> Explore
            </Link>
            <Link to="/daily-quiz" className="btn btn-outline">
              <FiTarget /> Daily Quiz
            </Link>
          </div>
        </section>

        {/* ================= ARTICLES ================= */}
        <section className="section">
          <h2>Latest Current Affairs</h2>

          {loading && <p>Loading articles...</p>}

          {!loading && today.length > 0 && (
            <>
              <h3 className="group-title">Today</h3>
              <div className="articles-grid">
                {today.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{
                      ...a,
                      dateLabel: getDateLabel(a.publishDate),
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && yesterday.length > 0 && (
            <>
              <h3 className="group-title">Yesterday</h3>
              <div className="articles-grid">
                {yesterday.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{
                      ...a,
                      dateLabel: getDateLabel(a.publishDate),
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && earlier.length > 0 && (
            <>
              <h3 className="group-title">Earlier</h3>
              <div className="articles-grid">
                {earlier.map((a) => (
                  <ArticleCard
                    key={a._id}
                    article={{
                      ...a,
                      dateLabel: getDateLabel(a.publishDate),
                    }}
                  />
                ))}
              </div>
            </>
          )}

          {!loading && articles.length === 0 && (
            <p>No articles found.</p>
          )}
        </section>

        {/* ================= TRENDING ================= */}
        {trending.length > 0 && (
          <section className="section">
            <h2>
              <FiTrendingUp /> Trending
            </h2>

            <div className="trending-grid">
              {trending.map((a, i) => (
                <Link
                  key={a._id}
                  to={`/article/${a.slug}`}
                  className="trending-item"
                >
                  <span className="trending-number">
                    #{i + 1}
                  </span>
                  <div>
                    <h3>{a.title}</h3>
                    <small>
                      {getDateLabel(a.publishDate)}
                    </small>
                  </div>
                </Link>
              ))}
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default Home;
