import { useState } from "react";

export default function MCQCard({ mcq }) {
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [answered, setAnswered] = useState(false);

  const handleAnswer = (index) => {
    if (answered) return; // prevent multiple clicks

    setSelectedIndex(index);
    setAnswered(true);
  };

  const getOptionStyle = (index) => {
    if (!answered) return "option";

    if (index === mcq.correctAnswer) {
      return "option correct";
    }

    if (index === selectedIndex) {
      return "option incorrect";
    }

    return "option";
  };

  return (
    <div className="mcq-card">
      {/* HEADER */}
      <div className="mcq-header">
        <span className="category">{mcq.category}</span>
        <span className={`difficulty ${mcq.difficulty?.toLowerCase()}`}>
          {mcq.difficulty}
        </span>
      </div>

      {/* QUESTION */}
      <h3 className="question">{mcq.question}</h3>

      {/* OPTIONS */}
      <div className="options">
        {mcq.options.map((option, index) => (
          <button
            key={index}
            className={getOptionStyle(index)}
            onClick={() => handleAnswer(index)}
            disabled={answered}
          >
            <span className="option-label">
              {String.fromCharCode(65 + index)}.
            </span>
            {option}
          </button>
        ))}
      </div>

      {/* RESULT */}
      {answered && (
        <div className="result">
          {selectedIndex === mcq.correctAnswer ? (
            <p className="correct-text">✅ Correct</p>
          ) : (
            <p className="incorrect-text">❌ Incorrect</p>
          )}
        </div>
      )}

      {/* EXPLANATION */}
      {answered && mcq.explanation && (
        <div className="explanation">
          <strong>Explanation:</strong>
          <p>{mcq.explanation}</p>
        </div>
      )}
    </div>
  );
}
